INSERT INTO `glosario` (`id`, `id_app`, `nombre`, `descripcion`, `imagen`) VALUES
(155, 12, 'Chamorro', 'Pantorrilla del cerdo que se prepara en cocido u horneada, al natural o bañada con salsa de chile pasilla. Esta parte del cerdo también es muy apreciada como parte de las carnitas.           			', 'chamorro'),
(156, 12, 'Chile ancho', 'Chile seco de color café rojizo, que mide en promedio 12 cm de largo y 7 cm en su parte más ancha. Tiene forma triangular y su piel es de textura rugosa y brillante. Los mejores son flexibles al tacto y nunca tiesos. Al remojarse adquiere un color rojo ladrillo, por ello es el encargado de colorear la gran mayoría de los guisos rojos. Es tal vez, el chile más utilizado en formas diferentes. Fresco es el chile poblano.          			', 'chile_ancho'),
(157, 12, 'Chicharrón ', 'Piel de cerdo oreada y frita, de textura crujiente, muy acostumbrada en México. \r\n             			', 'chicharron'),
(158, 12, 'Chile serrano', 'Chile fresco pequeño de forma cilíndrica, terminado en punta. Mide en promedio de 3 a 5 cm de largo y 1 de diámetro. En su gran mayoría se consume inmaduro, es decir, color verde, aunque al madurar se vuelve rojo y se utiliza de la misma manera. Se considera picoso y generalmente se ocupa con sus semillas y venas, también muy picosas. Posee cáscara tersa y brillante, nuca opaca o arrugada. El nombre de este chile se debe a que se empezó a cultivar en la sierra de Puebla, Hidalgo y Estado de México. También llamado chile verde; de hecho, cuando las recetas no mencionan el tipo de chile o simplemente dicen chile verde se sobrentiende que se trata del serrano. Conocido también como chile Altamira (Tamaulipas), chile cotlaxtla, chile costeño, chile Pánuco (Cotaxtla, Veracruz), chile serrano tampiqueño, chile tampiqueño (sierra de Puebla).', 'chile_serrano'),
(159, 12, 'Epazote', '(*Chenopodium ambrosioides*) Hierba aromática de la familia de las quenopodiáceas, nativa de Mesoamérica, empleada como tal desde la época prehispánica. La planta es ramosa, de hojas alternas, color verde claro, alargadas, elípticas y lanceoladas, irregularmente aserradas y ligeramente pecioladas. Su nombre deriva del náhuatl *epazotl*, de *epatl*, zorrillo y *tzotl*, suciedad, o sea suciedad de zorrillo. Esta denominación se debe a su olor cuando está crudo, que para muchos es desagradable. En realidad, su aroma tan especial no es notorio cuando se recolecta y sólo al cocerse y mezclarse con otros ingredientes se aprecia en plenitud. Conocido también como : apazote, pazote o *yepazotl* (náhuatl).', 'epazote'),
(160, 12, 'Mole', 'Del náhuatl *mulli*, salsa o guiso. Término que designa una infinidad de guisos complejos, por lo regular elaborados con una salsa espesa. El más conocido es un mole oscuro que en todo el país se conoce como mole poblano, aunque es importante aclarar que en el país se elaboran moles de colores, sabores y texturas diferentes, que son tan auténticos como el de la angélica ciudad. También define a sopas sustanciosas, como el mole de panza en Tlaxcala, el mole de olla o el michmole. Es sabido que en la época prehispánica se preparaban diversas salsas molidas y complejas, que al paso de los años y siglos se fueron modificando, refinando y adaptando a los nuevos ingredientes y técnicas culinarias.   ', 'mole'),
(161, 12, 'Frijol bayo', '(*Phaseolus vulgaris*) Variedad de frijol que puede tener tonos desde café claro hasta café oscuro. Como su nombre lo indica, la testa de los frijoles manchados presenta puntos de distintos colores; entre los más importantes están el canelo claro, el canelo oscuro, el vaquita y, en especial, el flor de mayo.             			', 'frijol_bayo'),
(162, 12, 'Huarache', '(o guarahce) \r\n\r\nAntojito de maíz similar al tlacoyo pero más grande y alargado, que se acostumbra servir con una gran variedad de ingredientes o guisados encima, como carnes de cerdo, res o pollo, huevo, tinga, longaniza, chorizo, nopales y mole, entre otros. Se cuenta que en 1938, la vendedora de tlacoyos, Carmen Gómez, adquirió un local en el mercado de Jamaica, ciudad de México y a partir de entonces, los tlacoyos adoptaron el nombre de huaraches, debido a su forma y tamaño, similares a los huaraches que se utilizaban antiguamente.          			', 'huarache'),
(163, 12, 'Chile de árbol', 'Chile alargado y delgado que mide en promedio 7 cm de largo y 1 de ancho. Cuando es fresco es de color verde y al madurar se torna rojo. De sabor picante, con cierta similitud a un chile serrano picoso. El chile de árbol seco es de color rojo brillante y muy picante, ésta es la forma en que más se consume. La planta que lo produce es más alta que otras variedades, mas no es un árbol.        			', 'chile_arbol'),
(164, 12, 'Chile cuaresmeño', 'Nombre con el que también se conoce al chile jalapeño.', 'chile_cuaresmeno'),
(165, 12, 'Chile guajillo', 'Chile seco color café rojizo, de piel tersa y brillante, forma triangular alargada; mide en promedio 10 cm de largo y cuatro en su parte más ancha. Es muy utilizado porque da color rojo y consistencia a los guisos, moles y salsas. Cuando está fresco se llama chile mirasol. También se le conoce como chile cascabel (Bajío, Chihuahua, huastecas), chile chilaca roja ancha (Estado de México), chile cuachalero (Colima, Jalisco), chile guajillo dulce  (Distrito Federal), chile guajillo que no pica (Distrito Federal), chile guajío (San Luis Potosí), chile guajón (San Luis Potosí, Zacatecas), chile mirasol (Jalisco), chile travieso, chile tres venas (San Luis Potosí).', 'chile_guajillo'),
(166, 12, 'Nopal', '(*Opuntia spp*) Nombre genérico que se emplea para designar diversas plantas del género Opuntia, originarias del continente americano. Son cactáceas con tallos o pencas tiernas, de forma ovalada, carnosas, aplanadas, delgadas, con espinas y líquido mucilaginoso que se conoce como baba. Entre las variedades domesticadas destacan: *Opuntia megacantha* y *Opuntia ficus-indica*, que es la variedad más utilizada para preparar nopalitos.  Siempre se procura eliminar la baba cociendo el nopal en abundante agua, al vapor, con limón o con cáscaras de tomate.', 'nopal'),
(167, 12, 'Cecina', 'Carne de res ligeramente salada y oreada; se puede secar al sol o mediante ahumado. Del latín *siccina*, carne seca. Suele comerse asada o frita, y es muy común en los estados del centro del país, Chiapas, Oaxaca y Veracruz.             			', 'cecina'),
(168, 12, 'Carne seca', 'Carne que se obtiene untándola con sal y dejándola orear hasta que se deshidrata durante unos ocho días, para poder conservarla por largo tiempo. Se ocupa de diferentes formas en los estados del norte del país, donde la carne más utilizada es la de res, pero antaño la de venado fue muy popular.', 'carne_seca'),
(169, 12, 'Chile cascabel', 'Chile seco de forma casi esférica y color café rojizo. Mide en promedio unos 3 cm de diámetro, y tiene cáscara tersa y dura. Es moderadamente picante, y de sabor agradable que recuerda ligeramente a la nuez. Cuando se agita este chile, sus semillas suenan como una sonaja o un cascabel, de ahí su nombre. Cuando está fresco se le llama chile bola.           			', 'chile_cascabel'),
(170, 12, 'Totopo', 'Tortilla de maíz cortada en triángulos  que se fríen hasta quedar crujientes.', 'totopitos'),
(171, 12, 'Chile habanero', '(*Capsicum chinense*) Chile fresco de color verde claro cuando joven, que se vuelve amarillo y más tarde anaranjado en su etapa madura. Tiene textura suave y forma globosa. Mide 4 cm de largo y tres de ancho y se considera el chile más picoso de todo el país. Es el chile clásico de la comida yucateca. De manera reciente se le otorgó la denominación de origen llamándole Chile Habanero de Yucatán.             			', 'chile_habanero'),
(172, 12, 'Plátano macho', 'Es el más grande de todos los plátanos; se ocupa inmaduro, maduro y muy maduro. Cuando está inmaduro es verde claro y al madurar se torna amarillo; en este punto es normal que la piel presente manchas negras. Cuando se empieza a pasar de maduro, la piel puede ponerse totalmente negra. Mide unos 30 cm de largo. Se come crudo, frito o cocido. Es también conocido como chapalote o zapalote.', 'platano_macho'),
(173, 12, 'Chile poblano', 'Chile fresco carnoso, de tamaño grande, de forma cónica aplanada con algunas ondulaciones. Generalmente es verde oscuro con piel brillante, aunque algunas variedades pueden ser más claras. Tiene un sabor particular que no se considera picante. El poblano de primera mide en promedio 12 cm de largo y 6 en su parte más ancha. Casi siempre se utiliza verde, al madurar torna a color rojo intenso, dejándolo secar se vuelve chile ancho. Es el chile más utilizado en todo el país, y del que más hectáreas se siembran. Aunque recibe distintos nombres regionales, se dice que los primeros cultivos formales de este chile comenzaron en el valle de Puebla, de ahí su nombre más común. Es conocido también como  chile ancho (Baja California), chile esmeralda, chile gordo, chile jaral (Estado de México), chile joto (Aguascalientes), chile miahuateco, chile para rellenar, chile pasilla fresco (Colima), chile pasilla verde (Colima), chile verdeño (Aguascalientes).', 'chile_poblano'),
(174, 12, 'Queso panela', 'Queso fresco elaborado con leche de vaca pasteurizada, aunque también se produce con una mezcla de leche de vaca y cabra. Es blanco, con textura porosa, suave y esponjosa. Su porcentaje graso es bajo y es muy popular en los estados del centro del país. Para su elaboración, la leche se escurre en canastas especiales que dejan la marca del tejido en la superficie del queso. El nombre de panela presumiblemente se debe a la forma tronco-cónica que adquiere al fabricarse, por lo que recuerda a la panela o piloncillo. Conocido también como asadera (Sinaloa), queso asadera, queso blanco, queso canasta, queso de canasta.', 'queso_panela'),
(175, 12, 'Bolillo', 'Pan salado de trigo en forma similar a un rombo, con una hendidura en el frente. Es crujiente por fuera y suave y esponjoso por dentro. Es una de las formas más populares del pan de sal.             			', 'bolillo'),
(176, 12, 'Manteca de cerdo', 'Grasa que se obtiene picando el unto del cerdo y poniéndolo a fuego bajo hasta que la mayor parte se derrite, entonces se filtra la grasa líquida y caliente del unto y se obtiene la manteca que, una vez fría, se solidifica. En los pueblos y ciudades de México se producen dos tipos: la oscura y la blanca. Esta última es más clara y por ello se expende más cara. Se utiliza mucho en la cocina tradicional mexicana para freír gran cantidad de antojitos y en la preparación de panes tradicionales, preparar la masa de tamales y para dar un sabor característico a distintos moles, pipianes y otros guisos típicos.', 'manteca_cerdo'),
(177, 12, 'Chile costeño ', 'Término con el cual se identifica a cualquier chile que crezca cerca de algunas de las dos grandes costas de México. El nombre puede resultar arbitrario; sin embargo, en diferentes lugares se encuentran chiles típicos a los que se llaman chiles costeños.            			', 'chile_costeno'),
(178, 12, 'Queso fresco', 'Término con el que se denomina a los quesos que no pasan por gran maduración o añejamiento. La mayoría de los quesos en el país son frescos. Por lo general tienen consistencia firme y sabor y textura suaves. ', 'queso_fresco'),
(179, 12, 'Machaca', 'Carne de res salada, secada al sol y machacada, típica del norte del país; puede ser gruesa o fina como pelusa. La machaca de calidad se elabora con lomo de res y no debe presentar nervios o pedazos grandes de grasa. La que se prepara con pulpa negra o aguayón es de inferior calidad, pero con muy buen sabor. Su nombre proviene de la acción de machacar la carne con una piedra o palo, en lugar de cortarla, molerla o hacerla picadillo. Se machaca (según el uso más tradicional) sobre un tronco de mezquite con un marro de madera del mismo árbol hasta lograr el grueso deseado y de nuevo se deja secar. Por lo regular la carne se sala y se deja secar, después se dora en las brasas, se remoja en agua para rehidratarla, retirarle el exceso de sal y ablandarla. Después de remojarla se exprime fuertemente y luego se fríe para prepararla en cualquier guiso. Según algunos autores, la machaca se ha elaborado en el norte de nuestro país desde el siglo XVIII, con el fin de conservar la carne para su consumo diario durante largos periodos.          			', 'machaca'),
(180, 12, 'Tortilla de harina', 'Tortilla que se prepara con masa de harina de trigo, a la que se añade manteca de cerdo o vegetal, sal y agua; los ingredientes se mezclan hasta lograr una masa homogénea que se deja reposar algunos minutos y se divide en bolitas. Luego se estira hasta obtener los círculos medianos y grandes que se cuecen en el comal por ambos lados. Es la tortilla más utilizada en los estados del norte del país. ', 'tortilla_harina'),
(181, 12, 'Huitlacoche', '(o cuitlacoche) \r\n\r\n(*Ustilago maydis*) Deriva del náhuatl cuitlatl, excremento y cochtli, dormido; es decir, excremento dormido. Hongo parásito que se desarrolla sobre las mazorcas tiernas del maíz. Aparece como un tumor en forma globosa, que infecta los granos de la mazorca, y es grisáceo por fuera y negro en su interior. Abunda de julio a septiembre en la temporada de lluvias en el centro del país. Es uno de los hongos más gustados para los mexicanos del centro del país, tal vez el más importante también. Se considera un ingrediente sofisticado.     			', 'huitlacoche'),
(182, 12, 'Panza de res', 'Estómago de res que se vende en los mercados populares crudo o cocido. Recibe diferentes nombres, dependiendo de la parte de los estómagos de la res de la que se trate. Los rumiantes tienen un sistema estomacal compuesto por cuatro porciones: la primera y más grande se localiza inmediatamente después del esófago: es el capote, bonete o panza y tiene forma de una bolsa. Del otro lado hay un área con hexágonos, llamada por ello panal; una tripa conduce al libro, que recibe este nombre pues tiene muchas membranas, una tras otra, como si fueran hojas de papel. Sigue otra bolsa alargada y con dos abultamientos: el primero se llama manzana y el segundo cuajo o cuajar. Por último se encuentra la tripa gorda.', 'pancita'),
(183, 12, 'Chile pasilla', 'Chile seco de forma alargada, de 15 a 20 cm de largo y de 2 a 3 de ancho, de color café negruzco, superficie brillante y arrugada, se dice que su nombre se debe a que se arruga como la uva pasa. Es moderadamente picante, y cuando está fresco es el chile chilaca. Conocido también como: chile achocolatado, chile negro (Michoacán, Baja California), chile pasilla mexicano (Oaxaca), chile pasilla de México (Oaxaca) o chile prieto (Veracruz).', 'chile_pasilla'),
(184, 12, 'Queso Chihuahua', 'Queso de pasta semidura elaborado con leche de vaca pasteurizada, no cocida. Tiene forma circular aplanada y un peso variable de 1 hasta 10 kg, de sabor y aroma delicado, color amarillo y consistencia que permite fundirlo fácilmente. Sus características, forma y elaboración proceden de los menonitas emigrados de Canadá. Se produce principalmente en Chihuahua . Conocido también como queso de rueda, queso menonita o queso rueda. ', 'queso_chihuahua'),
(185, 12, 'Chile chipotle', '(o chile chipocle) \n\nDel náhuatl chilli, chile, y poctli, humo, o sea chile ahumado. Chile seco y ahumado de color café oscuro, y de textura arrugada. Es uno de los chiles secos más picosos. Mide en promedio 6 cm de largo y unos 2.5 en su parte más ancha. Cuando está fresco se le llama chile jalapeño. La técnica de ahumarlos data de la época prehispánica.', 'chile_chipotle'),
(186, 12, 'Quesadilla', 'Antojito que consiste en una tortilla de masa de maíz doblada a la mitad en forma de media luna, rellena con queso fresco para derretir o algún otro guiso (tinga, papa, hongos, picadillo, ropa vieja, cuitlacoche, rajas, etc.) y cocida en comal o frita. En algunas regiones, especialmente en el norte del país, se pone a la quesadilla básica queso amarillo o queso Chihuahua y, en lugar de tortilla de maíz, se utiliza tortilla de harina de trigo. En los estados del sur y sureste del país, el término quesadilla se reserva exclusivamente para las rellenas de queso; a las demás se les llama empanadas.', 'quesadilla'),
(187, 12, 'Quelite cenizo', '(*Chenopodium album*) Planta robusta que puede alcanzar hasta 1 m de altura. Su tallo y sus ramas son estriadas y sus hojas ovadas, con los bordes ligeramente dentados y cubiertas de un polvo blanquecino al que debe su nombre. Se consume principalmente en el centro del país. Casi siempre las hojas y tallos tiernos se cuecen al vapor, se fríen y se consumen como los demás quelites, en salsas o ensaladas. Conocido también como bledo o quelite. Conocido en náhuatl de la sierra Norte de Puebla como: *nexuaquili* o *nexuaquilitl* o *nexuaug*.', 'quelite_cenizo'),
(188, 12, 'Requesón', 'Cuajada que se saca de los residuos del suero después de producido el queso. Se vende en las cremerías o queserías de los mercados populares. Se acostumbra comer en casi todo el país y se procura que esté fresco, ya que se descompone con rapidez.', 'requeson'),
(189, 12, 'Sope', 'Tortilla de maíz gruesa con el borde levantado o “pellizcado”, con el objetivo de que contenga dentro diversos ingredientes. El diámetro del sope varía de acuerdo con la región. Se utiliza para elaborar el antojito del mismo nombre.  El antojito, se elabora añadiendo diversos ingredientes según la región a la preparación de maíz. ', 'sope'),
(190, 12, 'Frijol refrito', 'Frijoles cocidos, machacados o licuados que se fríen con manteca de cerdo o aceite hasta que el caldo se ha evaporado y quede una pasta firme y suave que durante la cocción permita ver el fondo del sartén y la pasta se despegue con facilidad y forme una especie de bolillo o pan al sacarla de ahí. Es una de las formas más importantes y habituales de cocinar los frijoles en México.          			', 'frijol_refrito'),
(191, 12, 'Tamal', 'Del náhuatl *tamalli*. Preparación de origen prehispánico elaborada con masa de maíz batida; se rellena con salsa , algún tipo de carne, o sola; generalmente se envuelve en hojas de maíz o plátano, y se cuece al vapor. También se preparan tamales dulces. La forma de envolverlo varía, aunque predomina la alargada y gruesa con hoja de maíz, y la rectangular o cuadrada en hoja de plátano. También los hay triangulares como las corundas, o largos y gruesos, como el zacahuil.', 'tamal'),
(192, 12, 'Chile mulato', 'Chile seco color café negruzco, de piel gruesa y cuerpo carnoso, con forma y color parecidos a los del chile ancho. De sabor dulzón, con notas de chocolate, suave y sólo algunas veces picoso. Mide en promedio 12 cm de largo por 7 de ancho. Se obtiene de un tipo de chile poblano muy oscuro que casi nunca sale a la venta cuando está fresco, pues los agricultores lo conservan para secarlo y venderlo como chile mulato. Es muy importante en la preparación de los moles, especialmente en el poblano. Es fácil confundirlo con el chile ancho, para distinguirlos se deberán abrir y mirarse a trasluz: el ancho se verá color rojo y el mulato, café.', 'chile_mulato'),
(193, 12, 'Hoja santa', '(*Piper sanctum*) Planta aromática de la familia de las piperáceas. Sus hojas son verdes tienen forma acorazonada, textura suave, delgada, brillante en la cara superior y opaca en la inferior. Su tamaño varía, dependiendo del lugar donde crezca: pueden alcanzar entre los 12 y 25 cm de diámetro. Crece en lugares húmedos, de forma silvestre; también se cultiva en huertos familiares. Su aroma y sabor recuerdan al anís. Es originaria de México. Conocida también como acuyo, hoja santa, momo o tlanepa, entre otros nombres.    			', 'hoja_santa'),
(195, 12, 'Hoja de plátano asado', '(*Musa spp*) Hoja del árbol del plátano, de la familia de las musáceas. Es de gran tamaño, verde claro u oscuro, forma ovada con una costilla gruesa en el centro. Después de cortarla, se le retira la costilla y se emplea para envolver diferentes alimentos, especialmente tamales. Antes de utilizarla se pasa sobre un comal, parrilla o quemador caliente para suavizarla; en ocasiones se hierve en agua para obtener el mismo resultado.           			             			', 'hoja_platano'),
(196, 12, 'Hoja de maíz', '(o totomoxtle) \r\n\r\nDel náhuatl *totomochtli*. Hojas secas, rugosas y quebradizas que envuelven la mazorca del maíz. Por su resistencia se emplean para envolver tamales. Antes de usarse se deben remojar en agua, al menos por dos horas, para que se vuelvan flexibles. Se utilizan también para cubrir alimentos como la conserva del torno largo, achiote y piloncillo, entre otros. Su uso data de la época prehispánica. En la península de Yucatán se le conoce como joloch del maya *ho’loch*, de ahí que envolver un tamal en dicha región se denomine enjolochar. \r\n		', 'hoja_maiz'),
(197, 12, 'Tasajo ', '(Cecina)\r\n\r\nCarne de res ligeramente salada y oreada; se puede secar al sol o mediante ahumado. En Oaxaca a la cecina se la llama tasajo y también está cortada en tiras, pero suele ser una carne ligeramente más gruesa, salada y dura que la del centro del país. El tasajo oaxaqueño casi siempre es de carne de res, aunque en ocasiones se prepara con cerdo. La carne se pasa por agua caliente o se cuece antes de asarla o freírla.', 'tasajo'),
(198, 12, 'Capulín', 'Del náhuatl *capolin* o *capulín*. Frutos de familias diferentes con similares características, entre los cuales se consumen las siguientes variedades: *Ardisia compressa*, *Ardisia escallonoides*, *Eugenia acapulcensis*, *Muntigia calabura*, *Prunus serotina*, *Vitex pyramidata*.             			', 'capulin'),
(199, 12, 'Tuna', 'Fruto ovalado o periforme del nopal, con cáscara gruesa y pequeñas espinas que parecen pelusa. Todo su interior está lleno de una pulpa carnosa, suave y dulce, con semillas comestibles. Se consume como fruta fresca. Suele ser muy dulce y consumirse en temporada de calor. Existen muchas variedades que se consumen en México, entre las que encontramos  la tuna reina (*Opuntia albicarpa*), tuna tapona (*Opuntia robusta*) y la tuna cardona (*Opuntia streptacantha*). ', 'tuna'),
(200, 12, 'Nata', 'Grasa que se encuentra emulsionada en la leche bronca o cruda; al dejar reposar la leche durante cierto tiempo, la grasa se separa de la leche y flota en la superficie; de esta forma se obtiene la nata, que se utiliza en varias regiones de México como ingrediente para preparar un sinnúmero de panes, galletas, gorditas y postres, o bien, se bate con sal para elaborar mantequilla.           			', 'nata'),
(201, 12, 'Polvorón', 'Panecillo o galleta dura, quebradiza. En ocasiones su masa es muy suave y en otras crujiente. Su origen es morisco y llegó a México después de la Conquista. En México existen muchas variedades.', 'polvoron'),
(202, 12, 'Azúcar glass', '(o azúcar lustre)\r\n\r\nEste azúcar cristalizado, molido muy fino hasta formar un polvo impalpable, incluye un 3% de almidón para evitar que cuaje en bloque. Sirve para espolvorear, recubrir o decorar los pasteles horneados e interviene con frecuencia en la pastelería.         			', 'azucar_glass'),
(203, 12, 'Piloncillo', '(o panela  o panocha)\r\n\r\nEdulcorante elaborado a partir de melcocha prieta o miel de azúcar no clarificada en forma de cono truncado. Los indígenas de la sierra de Puebla exprimen la caña de azúcar en trapiches de madera o metal, cuecen el jugo por varias horas; cuando está espeso lo baten y lo vacían en moldes de barro con forma de triángulo truncado; una vez endurecido lo envuelven en hojas de caña para conservarlo. Es un endulzante fácil de encontrar en los mercados populares. Se utiliza en la mayoría de los dulces regionales de México.', 'piloncillo'),
(204, 12, 'Uchepo', '(huchepo o uchepu) Nombre purépecha con el que se conoce un tamal en hoja de elote fresca. Casi siempre pequeño, se prepara con granos de elote frescos, molidos y mezclados con leche, azúcar, nata o crema y sal. Aunque puede tener un sabor ligeramente dulce, no se considera tamal dulce como la mayoría de los tamales de elote del resto del país. ', 'uchepo'),
(205, 12, 'Atole', '(o atol) \r\n\r\nBebida caliente y espesa hecha a base de maíz cocido, molido y desleído en agua. El agua y el maíz molido se colocan sobre el fuego dentro de una olla de barro o metal, sin dejar de mover hasta que la mezcla se cuece y espesa. El atole es una de las bebidas más populares en todo México. En cada región se prepara con diferentes ingredientes, de modo que es casi infinita la variedad de sabores de atoles.', 'atole'),
(206, 12, 'Chocolate de metate', 'Preparación de granos de cacao secos y tostados, molidos en metate y mezclados con un poco de azúcar, canela y almendra. Primero se muele la almendra y la canela, después se retira del metate y se empieza a moler el cacao. Antes de moler el cacao, es indispensable colocar una vela bajo el metate para que se caliente previamente la piedra, de esta forma el cacao se muele y derrite al mismo tiempo. Cuando el cacao está totalmente líquido, se añade el azúcar, la canela y las almendras; todo toma forma de masa homogénea muy suave y caliente. Mientras se enfría, poco a poco se hacen las tablillas o bolas de chocolate, que al enfriarse totalmente estarán duras y solamente se podrán derretir al mezclarse con agua o leche caliente. Estrictamente se le llama chocolate de metate al que se prepara con este procedimiento. ', 'chile_metate'),
(207, 12, 'Pingüica', '(*Arctostaphylos pungens*) Fruto en forma de drupa. Pertenece a la familia de las ericánceas; mide entre 5 y 8 mm de diámetro. La pingüica es lisa, de color rojo oscuro o rojo amarillento y sabor agridulce; crece en un arbusto que mide entre 2 y 4 metros de altura. Se consume en el centro del país cuando está seca. ', 'pinguica'),
(208, 12, 'Queso añejo', 'Denominación genérica para los quesos madurados. Su consistencia por lo general es dura y su sabor concentrado y salado. Son elaborados a partir de leche de vaca y sus formas y peso pueden variar. Entre ellos, el queso Cotija, algunos enchilados, sierra, de sal, Chapingo, menonita, refregado y de Chiautla.', 'queso_anejo'),
(210, 12, 'Tortilla de maiz', 'Disco plano de masa cocida de nixtamal que se elabora a mano o en una máquina tortilladora. De origen prehispánico, los mexicas la denominaban tlaxcalli, preparándola de muy diversas formas y tamaños. Hoy en día la tortilla de tamaño estándar mide entre 12 y 18 cm de diámetro y pesa entre 30 y 40 gramos. La manera de tradicional de preparar la realizan las mujeres que tortean la masa: convierten una pequeña bola de masa de maíz en un disco delgado y redondo, que al tener contacto con el calor del comal se infla lentamente. Las tortillas poseen un derecho y un revés; el primero tiene adherida la piel, y el revés es el lugar donde se añaden los ingredientes y se reconoce por tener una piel delgada, desprendible y delicada.', 'tortilla_maiz'),
(211, 12, 'Chile jalapeño', 'Chile fresco color verde claro u oscuro, de forma cónica alagada, con terminación chata. Es carnoso con piel brillante y mide en promedio unos 6 cm de largo y 3 de ancho. Puede ser moderadamente picante o muy picante. El chile jalapeño se vuelve color rojo intenso cuando madura, y se usa indistintamente como el verde. Es ampliamente cultivado en diferentes regiones del país, por lo que se le conoce con varios nombres locales aunque el nombre de chile jalapeño es el más común en todo el país; su razón de ser es que, según se dice, antiguamente se cultivaba en Xalapa, desde donde se comercializaba a otras partes.\r\n', 'chile_jalapeno'),
(216, 12, 'Mole negro', 'Salsa compleja, moderadamente picante o incluso dulzona. Entre sus principales ingredientes encontramos chiles chilhuacle negro, chilhuacle rojo, mulato, pasilla oaxaqueño y pasilla de México, tortilla quemada, cebolla, ajo, pan de yema, plátano macho, ajonjolí, cacahuates, nueces, nuez moscada, almendras, pepitas de calabaza, pasitas, jitomate, miltomate, canela, orégano, tomillo, mejorana, anís, comino, clavo, pimienta gorda, azúcar, hoja de aguacate chocolate de metate y manteca de cerdo. El tono negro  de la salsa se debe  a la utilización de los chiles ya mencionados, pero en especial a dos técnicas muy precisas: quemar los chiles guajillos, sus semillas y las tortillas de maíz hasta que se incendien, después se someten a un largo proceso de oreado y remojado para quitarles el sabor amargo, finalmente se muelen y se ocupan como tinta negra en la salsa, además de aportar  el sabor terroso y ceniciento que lo hace mágico. Es uno de los  siete moles de Oaxaca que se prepara principalmente en la región de los Valles Centrales; fuera del estado se conoce como mole oaxaqueño.', 'mole_negro'),
(217, 12, 'Chile mora', 'Chile seco ahumado de color morado, de ahí su nombre; es muy picante, mide unos 5 cm de largo. Es en realidad un chile chipotle, pero por sus características, tamaño, sabor, se le identifica de forma distinta, aunque se emplea de la misma manera. Se conoce en Veracruz y Puebla como chipotle mora.', ''),
(218, 12, 'Colimote', 'Gentilicio de la capital de Colima, estado ubicado en la región occidental del país.', ''),
(219, 14, 'Termino de glosario 1', 'Aquí va la descripción', 'imagen_prueba'),
(220, 14, 'Termino de glosario 2', 'Descripción', 'imagen_2'),
(221, 14, 'Termino de glosario 3', 'Descripción 2', 'imangen_3'),
(222, 14, 'Termino de glosario 4', 'Descripción', 'imagen_3');
INSERT INTO `recetas` (`id`, `titulo`, `id_categoria`, `id_app`, `procedimiento`, `ingredientes`, `preparacion`, `coccion`, `costo`, `foto`, `user_fav`, `dificultad`, `preparada`) VALUES
(58, 'Cecina', 10, 11, '   <p> &nbsp;  &nbsp;  &nbsp;  &nbsp;  &nbsp;  &nbsp;   “Cecina</p>\n\n<p>• Ase la cecina en una parrilla con un poco de aceite. <br />\nEnsalada de nopales y habas<br />\n• Mezcle todos los ingredientes y reserve.</p>\n\n<p><br />\nSalsa de chile ancho</p>\n\n<p>• Hierva en un poco de agua los chiles ancho para hidratarlos; cuando estén suaves, lícuelos con los demás ingredientes y reserve.</p>\n\n<p><br />\nPresentación</p>\n\n<p>• Sirva la cecina acompañada del queso, las cebollas cambray, el aguacate, la ensalada de nopales y habas y la salsa de chile ancho.”<br /></p>', '   <p> &nbsp;  &nbsp;  &nbsp;  &nbsp;  &nbsp;  &nbsp;   “Cecina</p>\n\n<p>1 kg de cecina<br />\nc/s de aceite</p>\n\n<p><br />\nEnsalada de nopales y habas</p>\n\n<p>6 nopales cocidos en agua con sal <br />\ny cortados en cubos<br />\n300 g de habas frescas cocidas, sin cáscara<br />\n3 jitomates pelados, sin semillas y cortados en cubos pequeños<br />\n1 cebolla picada finamente<br />\n½ taza de cilantro picado<br />\n4 cucharadas de aceite de oliva<br />\n4 cucharadas de vinagre blanco<br />\n2 cucharadas de orégano<br />\nqueso fresco desmoronado al gusto (opcional)<br />\nsal al gusto</p>\n\n<p><br />\nSalsa de chile ancho</p>\n\n<p>6 chiles ancho sin semillas <br />\nni venas<br />\n1 jitomate asado<br />\n2 dientes de ajo asados<br />\n1 cucharadita de orégano<br />\n¼ de raja de canela asada<br />\n3 cucharadas de vinagre <br />\nde manzana<br />\nsal al gusto</p>\n\n<p><br />\nPresentación</p>\n\n<p>300 g de queso fresco cortado <br />\nen rebanadas delgadas (opcional)<br />\n18 cebollas cambray asadas<br />\n1 aguacate cortado en rebanadas (opcional)”<br /></p>', 20, 15, 6, 'saladas_02', 0, 2, 0),
(59, 'Chicharrón en salsa verde', 10, 11, '<p>&nbsp;  &nbsp;  &nbsp;  &nbsp;  &nbsp;  &nbsp;  &nbsp; &#8220;Salsa verde</p>\n\n<p>• Ase a fuego medio los chiles, los tomates, la cebolla y el ajo.<br />\n• Pele el ajo y licue los ingredientes asados con el cilantro y sal. Si es necesario, agregue un poco de caldo de pollo para obtener una consistencia más ligera.<br />\n• Caliente la manteca en una cacerola a fuego alto, vierta la salsa y cocine por 5 minutos moviendo de vez en cuando para que no se pegue.</p>\n\n<p><br />\nPresentación</p>\n\n<p>• Añada el chicharrón a la salsa y cueza hasta que éste se ablande; verifique la sal.<br />\n• Sirva caliente acompañado con los frijoles de la olla y las tortillas de maíz.</p>\n\n<p>Éste es un platillo para desayunar muy acostumbrado en la zona centro del país. <br />\nSe consume por igual en la mañana o al mediodía.&#8221;<br /></p>', '<p>&nbsp;  &nbsp;  &nbsp;  &nbsp;  &nbsp;  &nbsp;  &nbsp; &#8220;Salsa verde</p>\n\n<p>4 chiles serrano verdes<br />\n750 g de tomates troceados<br />\n1 cebolla<br />\n1 diente de ajo grande con piel<br />\n½ taza de cilantro troceado<br />\nsal al gusto<br />\nc/s de caldo de pollo<br />\n3 cucharadas de manteca de cerdo</p>\n\n<p><br />\nPresentación</p>\n\n<p>350 g de chicharrón de cerdo cortado <br />\nen trozos<br />\nfrijoles de la olla al gusto (ver pág. 18)<br />\ntortillas de maíz al gusto&#8221;<br /></p>', 10, 15, 1, 'saladas_03', 0, 1, 0),
(60, 'Chilaquiles colimotes', 10, 11, '<p>&nbsp;  &nbsp;  &nbsp;  &nbsp;  &nbsp;  &nbsp;  &nbsp; &#8220;Salsa roja</p>\n\n<p>• Cueza los jitomates con los chiles y el agua; retire del fuego y deje enfriar.<br />\n• Licue los jitomates con el agua de cocción, los chiles, la cebolla y los ajos hasta obtener una salsa tersa y fina.<br />\n• Caliente el aceite en una olla y vierta la salsa. Deje cocer durante algunos minutos y agregue sal. Reserve caliente.</p>\n\n<p><br />\nChilaquiles</p>\n\n<p>• Fría los triángulos de tortilla en abundante aceite caliente. Cuando estén ligeramente dorados, retire el aceite y vierta los huevos batidos; mezcle hasta que estén cocidos.<br />\n• Añada la salsa y deje cocinar por unos minutos más; verifique la sal y retire del fuego.</p>\n\n<p><br />\nPresentación</p>\n\n<p>• Sirva los chilaquiles acompañados del queso añejo, la crema y la cebolla.</p>\n\n<p>En un clásico desayuno colimense, no pueden faltar estos chilaquiles, un tamal <br />\nde ceniza y un birote. Todo puede disfrutarse mejor con un vaso de tuba.&#8221;<br /></p>', '<p>&nbsp;  &nbsp;  &nbsp;  &nbsp;  &nbsp;  &nbsp;  &nbsp; &#8220;Salsa roja</p>\n\n<p>½ kg de jitomates<br />\n2 chiles jalapeño<br />\n2 tazas de agua<br />\n½ cebolla<br />\n2 dientes de ajo<br />\n2 cucharadas de aceite<br />\nsal al gusto</p>\n\n<p><br />\nChilaquiles</p>\n\n<p>12 tortillas de maíz cortadas en triángulos pequeños<br />\nc/s de aceite para freír<br />\n6 huevos batidos ligeramente<br />\nsal al gusto</p>\n\n<p><br />\nPresentación</p>\n\n<p>½ taza de queso añejo rallado<br />\n½ taza de crema<br />\n½ cebolla fileteada&#8221;<br /></p>', 10, 15, 1, 'saladas_04', 0, 2, 0),
(61, 'Chilaquiles verdes con pollo', 10, 11, '<p>&nbsp;  &nbsp;  &nbsp;  &nbsp;  &nbsp;  &nbsp;  &nbsp; &#8220;Salsa</p>\n\n<p>• Cueza los tomates con los chiles y el caldo de pollo. Retire del fuego, deje enfriar y lícuelos con la cebolla, los ajos y el cilantro hasta obtener una salsa tersa.<br />\n• Caliente el aceite en una olla y fría la salsa. Pruebe y ajuste la cantidad de sal. En el último hervor añada la rama de epazote.<br />\nChilaquiles<br />\n• Sumerja en la salsa los triángulos de tortilla fritos; deje hervir un par de minutos y retire del fuego.</p>\n\n<p><br />\nPresentación</p>\n\n<p>• Sirva inmediatamente acompañados con el pollo deshebrado, el queso fres­co, la crema y la cebolla.</p>\n\n<p>Si desea, sustituya el pollo por huevo. Gratínelos con queso Chihuahua <br />\no manchego antes de servir.&#8221;<br /></p>', '<p>&nbsp;  &nbsp;  &nbsp;  &nbsp;  &nbsp;  &nbsp;  &nbsp; &#8220;Salsa</p>\n\n<p>½ kg de tomates<br />\n6 chiles serrano verdes<br />\n2 tazas de caldo de pollo<br />\n½ cebolla<br />\n2 dientes de ajo<br />\n½ taza de cilantro troceado<br />\n3 cucharadas de aceite<br />\nsal al gusto<br />\n1 rama de epazote</p>\n\n<p><br />\nChilaquiles</p>\n\n<p>½ kg de tortillas de maíz cortadas <br />\nen triángulos y fritas</p>\n\n<p><br />\nPresentación</p>\n\n<p>1 pechuga de pollo cocida y deshebrada<br />\n1 taza de queso fresco rallado<br />\n½ taza de crema (opcional)<br />\n½ cebolla cortada fileteada&#8221;<br /></p>', 10, 15, 1, 'saladas_05', 0, 2, 0),
(62, 'Enfrijoladas', 10, 11, '<p>&nbsp;  &nbsp;  &nbsp;  &nbsp;  &nbsp;  &nbsp;  &nbsp; &#8220;Frijoles de la olla</p>\n\n<p>• Coloque los frijoles, la cebolla, el diente de ajo y 1 litro de agua dentro de una olla de presión, cierre y ponga sobre el fuego. Cuente de 30 a 35 minutos de cocción a partir de que empiece a escapar el vapor de la olla.<br />\n• Retire del fuego, deje enfriar por completo la olla, destápela y añada la manteca, el epazote, sal, y si lo desea, el agua restante. Ponga a hervir nuevamente para que se integren los sabores. Reserve.</p>\n\n<p><br />\nEnfrijoladas</p>\n\n<p>• Licue los frijoles de la olla con un poco de su caldo y el chile chipotle. Ca­liente una cacerola con la manteca y cuando humee vierta los frijoles molidos. Añada la rama de epazote y deje hervir durante 5 minutos. Retire el epazote y reserve. Al final deberá obtener una salsa ligeramente espesa.<br />\n• Caliente el aceite y pase dentro de él las tortillas una a una por unos segundos, sólo para que se suavicen.</p>\n\n<p><br />\nPresentación</p>\n\n<p>• Acomode 3 tortillas en un plato, rellénelas con el pollo y enróllelas.<br />\n• Bañe con los frijoles molidos y acompañe con la crema, el queso y la ce­bolla.&#8221;<br /></p>', '<p>&nbsp;  &nbsp;  &nbsp;  &nbsp;  &nbsp;  &nbsp;  &nbsp; &#8220;Frijoles de la olla</p>\n\n<p>250 g de frijoles limpios, remojados desde la noche anterior y drenados<br />\n½ cebolla<br />\n1 diente de ajo<br />\n1½ l de agua<br />\n1½ cucharadas de manteca de cerdo<br />\n1 rama de epazote<br />\nsal al gusto</p>\n\n<p><br />\nEnfrijoladas</p>\n\n<p>1 chile chipotle en escabeche<br />\n2 cucharadas de manteca de cerdo<br />\n1 rama de epazote<br />\nc/s de aceite para freír<br />\n12 tortillas de maíz</p>\n\n<p><br />\nPresentación</p>\n\n<p>½ pechuga de pollo cocida y deshebrada<br />\n1 taza de crema<br />\n150 g de queso fresco<br />\n½ cebolla picada finamente&#8221;<br /></p>', 15, 45, 2, 'saladas_06', 0, 2, 0),
(63, 'Enmoladas', 10, 11, '<p>&nbsp;  &nbsp;  &nbsp;  &nbsp;  &nbsp;  &nbsp;  &nbsp; &#8220;Enmoladas</p>\n\n<p>• Caliente el caldo de pollo y disuelva el mole hasta que obtenga una consistencia no muy espesa. Agregue sal si fuera necesario.<br />\n• Caliente el aceite y pase dentro de él las tortillas una a una por unos segundos sólo para que se suavicen.</p>\n\n<p><br />\nPresentación</p>\n\n<p>• Introduzca una a una las tortillas en el mole, rellénelas con la pechuga de pollo y dóblelas a la mitad. Sirva tres enmoladas por plato y termine bañándolas con mole. Agregue la crema y el queso; acompañe con los frijoles bayos refritos.</p>\n\n<p>Esta preparación forma parte de la gran familia de enchiladas del país. <br />\nSon tradicionales las elaboradas con mole poblano, pero pueden prepararse <br />\ncon cualquier otro tipo de mole: negro, almendrado o incluso de Xico.&#8221;<br /></p>', '<p>&nbsp;  &nbsp;  &nbsp;  &nbsp;  &nbsp;  &nbsp;  &nbsp; &#8220;Enmoladas</p>\n\n<p>750 ml de caldo de pollo<br />\n300 g de mole en pasta<br />\nsal al gusto<br />\nc/s de aceite para freír<br />\n12 tortillas de maíz</p>\n\n<p><br />\nPresentación</p>\n\n<p>1 pechuga de pollo cocida y deshebrada<br />\n½ taza de crema<br />\n50 g de queso fresco desmenuzado<br />\nfrijoles bayos refritos al gusto&#8221;<br /></p>', 10, 15, 1, 'saladas_07', 0, 2, 0),
(64, 'Huarache con costilla', 10, 11, '<p>&nbsp;  &nbsp;  &nbsp;  &nbsp;  &nbsp;  &nbsp;  &nbsp; &#8220;Huarache</p>\n\n<p>• Mezcle la masa con un poco de agua y sal.<br />\n• Elabore bolas con la masa y deles forma alargada. Aplánelas entre 2 ho­jas de plástico hasta que tengan ½ centímetro de grosor.<br />\n• Fría los huaraches en suficiente aceite.</p>\n\n<p><br />\nPresentación</p>\n\n<p>• Unte a cada huarache frijoles refritos, agregue salsa verde, cebolla, queso y acompañe con un trozo de costillita. Sírvalos inmediatamente.</p>\n\n<p>Este desayuno es clásico para los domingos. En los mercados populares y en los establecimientos donde venden quesadillas, se llegan a ofrecer huaraches de proporciones enormes con un bistec de costilla de igual tamaño o un huevo frito.&#8221;<br /></p>', '<p>&nbsp;  &nbsp;  &nbsp;  &nbsp;  &nbsp;  &nbsp;  &nbsp; &#8220;Ingredientes para 4 porciones:</p>\n\n<p>Huarache</p>\n\n<p>300 g de masa para tortillas<br />\nc/s de agua<br />\nsal al gusto<br />\nc/s de aceite para freír</p>\n\n<p><br />\nPresentación</p>\n\n<p>1 taza de frijoles refritos<br />\n1 taza de salsa verde cruda<br />\n½ cebolla picada finamente<br />\n1 taza de queso rallado<br />\n½ kg de costillitas de cerdo cocidas y fritas&#8221;<br /></p>', 15, 10, 2, 'saladas_08', 0, 2, 0),
(65, 'Huevos a la albañil', 10, 11, '<p>&nbsp;  &nbsp;  &nbsp;  &nbsp;  &nbsp;  &nbsp;  &nbsp; &#8220;Huevos</p>\n\n<p>• Caliente un sartén con el aceite, agregue sal a los huevos y hágalos revueltos; retírelos del sartén y reserve.</p>\n\n<p><br />\nSalsa</p>\n\n<p>• Licue los jitomates con la cebolla, el diente de ajo y los chiles de árbol. Ca­liente el aceite en el mismo sartén donde preparó los huevos y vierta la salsa; cueza durante 2 minutos y verifique la sal.<br />\n• Agregue los huevos y deje hervir durante 2 minutos más.</p>\n\n<p><br />\nPresentación</p>\n\n<p>• Sirva acompañados con los totopos, los frijoles refritos y el queso.</p>\n\n<p>El adjetivo “a la albañil” indica la personalidad picante de este desayuno. <br />\nPara esta receta se ha reducido la cantidad de chiles de árbol, pero, si lo desea, <br />\nes posible hacer honor al nombre agregando más.&#8221;<br /></p>', '<p>&nbsp;  &nbsp;  &nbsp;  &nbsp;  &nbsp;  &nbsp;  &nbsp; &#8220;Ingredientes para 3 porciones:</p>\n\n<p>Huevos</p>\n\n<p>3 cucharadas de aceite<br />\n6 huevos batidos ligeramente<br />\nsal al gusto</p>\n\n<p><br />\nSalsa</p>\n\n<p>2 jitomates asados<br />\n¼ de cebolla asada<br />\n1 diente de ajo asado<br />\n2 chiles de árbol secos asados<br />\n2 cucharadas de aceite<br />\nsal al gusto</p>\n\n<p><br />\nPresentación</p>\n\n<p>totopos al gusto<br />\nfrijoles refritos al gusto (ver pág. 40)<br />\nqueso fresco desmoronado, al gusto&#8221;<br /></p>', 5, 10, 1, 'saladas_09', 0, 1, 0),
(67, 'Huevos ahogados', 10, 11, '<p>&nbsp;  &nbsp;  &nbsp;  &nbsp;  &nbsp;  &nbsp;  &nbsp; &#8220;Salsa</p>\n\n<p>• Licue los jitomates, la cebolla, los chiles guajillo, los dientes de ajo, el caldo de pollo y sal.<br />\n• Caliente el aceite en una cacerola extendida y vierta la salsa; cueza durante 5 minutos y verifique la sal.</p>\n\n<p><br />\nHuevos</p>\n\n<p>• Baje el fuego al mínimo y vierta con cuidado los huevos uno a uno a la salsa, procurando que queden enteros y separados entre ellos. Unos minutos antes de que los huevos se cuezan, agregue los nopales, procurando no romper las yemas.<br />\n• Retire del fuego y sirva.</p>\n\n<p>El curioso nombre de este platillo se debe a que los huevos “nadan” <br />\nen un caldillo de jitomate.&#8221;<br /></p>', '<p>&nbsp;  &nbsp;  &nbsp;  &nbsp;  &nbsp;  &nbsp;  &nbsp; &#8220;Ingredientes para 3 porciones:</p>\n\n<p>Salsa</p>\n\n<p>6 jitomates asados<br />\n½ cebolla asada<br />\n3 chiles guajillo desvenados, asados <br />\ny remojados (opcional)<br />\n2 dientes de ajo asados<br />\n1 l de caldo de pollo<br />\nsal al gusto<br />\nc/s de aceite</p>\n\n<p><br />\nHuevos</p>\n\n<p>6 huevos<br />\n1½ tazas de nopales cortados en tiras <br />\ny cocidos&#8221;<br /></p>', 5, 5, 2, 'saladas_11', 0, 1, 0),
(68, 'Huevos aporreados', 10, 11, '<p>&nbsp;  &nbsp;  &nbsp;  &nbsp;  &nbsp;  &nbsp;  &nbsp; &#8220;Huevos</p>\n\n<p>• Caliente en un sartén 1 cucharada de aceite y sofría la carne seca o cecina. Cuando esté dorada, retire del fuego y córtela en cuadros pequeños; re­serve.<br />\n• Caliente el aceite restante en el mismo sartén y acitrone la cebolla, el ajo, los jitomates y los chiles. Añada la cecina, los huevos y sal; mezcle y retire del fuego cuando todo esté cocido.</p>\n\n<p><br />\nSalsa de chile cascabel</p>\n\n<p>• Ase a fuego bajo los chiles por el lado brillante. Retírelos del fuego y deje enfriar.<br />\n• Coloque los jitomates asados en una olla pequeña, cúbralos con el agua y cue­za por 15 minutos. Deje enfriar y lícuelos con los chiles, los ajos, la sal y el agua hasta obtener una salsa martajada.<br />\n• Si lo desea, caliente un poco de aceite y fría la salsa por 10 minutos.</p>\n\n<p><br />\nPresentación</p>\n\n<p>• Sirva los huevos acompañados de la salsa de chile cascabel y las tortillas de maíz.</p>\n\n<p>Esta preparación, típica de Tierra Caliente en Michoacán y Guerrero, <br />\ntambién puede llamarse aporreada o aporreado. La peculiaridad consiste <br />\nen la utilización de carne seca, la cual se corta o desmenuza y luego se golpea para abrir las fibras de la carne antes de utilizarla.&#8221;<br /></p>', '<p>&nbsp;  &nbsp;  &nbsp;  &nbsp;  &nbsp;  &nbsp;  &nbsp; &#8220;Ingredientes para 4 porciones:</p>\n\n<p>Huevos</p>\n\n<p>250 g de carne seca o cecina<br />\n3 cucharadas de aceite<br />\n1 cebolla picada finamente<br />\n1 diente de ajo picado finamente<br />\n3 jitomates picados<br />\n2 chiles serranos sin venas ni semillas picados finamente<br />\n8 huevos batidos ligeramente<br />\nsal al gusto</p>\n\n<p><br />\nSalsa de chile cascabel</p>\n\n<p>6 chiles cascabel limpios sin semillas <br />\nni venas<br />\n2 jitomates asados<br />\n¾ de taza de agua<br />\n2 dientes de ajo grandes asados, pelados <br />\ny troceados<br />\n1 cucharadita de sal</p>\n\n<p><br />\nPresentación</p>\n\n<p>tortillas de maíz al gusto&#8221;<br /></p>', 10, 25, 2, 'saladas_12', 0, 4, 0),
(69, 'Huevos motuleños', 10, 11, '<p>&nbsp;  &nbsp;  &nbsp;  &nbsp;  &nbsp;  &nbsp;  &nbsp; &#8220;Huevos</p>\n\n<p>• Caliente el aceite en un sartén y pase una a una las tortillas dentro de él sólo para que se suavicen. Colóquelas sobre papel absorbente para eliminar el exceso de grasa y reserve.<br />\n• Haga unos huevos estrellados en el mismo sartén, agregue sal. Si fuera necesario, añada más aceite entre cada huevo.<br />\n• Unte sobre cada tortilla un poco de frijoles refritos y ponga encima 1 huevo.</p>\n\n<p><br />\nSalsa</p>\n\n<p>• Licue los jitomates, la cebolla, el ajo, el vinagre y el agua.<br />\n• Caliente en una cacerola el aceite y vierta la salsa. Añada la hoja de laurel, el chile habanero y sal; deje hervir a fuego bajo durante 5 minutos sin dejar de mover. Retire del fuego, deseche la hoja de laurel, verifique la sal y reserve.</p>\n\n<p><br />\nPresentación</p>\n\n<p>• Acompañe los huevos con la salsa y las guarniciones.</p>\n\n<p>Tradicionalmente estos huevos se bañan con la salsa, <br />\npero puede servirla en un recipiente por separado.&#8221;<br /></p>', '<p>&nbsp;  &nbsp;  &nbsp;  &nbsp;  &nbsp;  &nbsp;  &nbsp; &#8220;Ingredientes para 3 porciones:</p>\n\n<p>Huevos</p>\n\n<p>6 tortillas de maíz<br />\n½ taza de aceite<br />\n6 huevos<br />\n1 taza de frijoles refritos (ver pág. 40)<br />\nsal al gusto</p>\n\n<p><br />\nSalsa</p>\n\n<p>2 jitomates<br />\n¼ de cebolla<br />\n1 diente de ajo<br />\n1 cucharadita de vinagre<br />\n1 taza de agua<br />\n2 cucharadas de aceite<br />\n1 hoja de laurel<br />\n1 chile habanero<br />\nsal al gusto</p>\n\n<p><br />\nGuarniciones</p>\n\n<p>100 g de jamón cortado en cubos pequeños<br />\n100 g de chícharos cocidos<br />\n100 g de queso fresco rallado<br />\n1 plátano macho cortado en rebanadas <br />\ny fritas<br />\n&#8220;</p>', 5, 15, 2, 'saladas_13', 0, 3, 0),
(70, 'Huevos rabo de mestiza', 10, 11, '<p>&nbsp;  &nbsp;  &nbsp;  &nbsp;  &nbsp;  &nbsp;  &nbsp; &#8220;Salsa</p>\n\n<p>• Licue los jitomates con el ajo y el agua. Caliente el aceite en una cacerola extendida y fría el molido; deje cocinar durante unos minutos.<br />\n• Agregue las rajas de chile, la cebolla y sal; cueza durante 3 minutos.</p>\n\n<p><br />\nHuevos</p>\n\n<p>• Añada los huevos, uno a uno, evitando que se junten y que se estrellen las yemas. Deje hervir hasta que estén cocidos.</p>\n\n<p><br />\nPresentación</p>\n\n<p>• Sirva acompañados con los cubos de queso.</p>\n\n<p>Tradicionalmente este platillo se elabora con rajas de chile poblano, pero puede sustituirlas por rajas de pimiento morrón en caso de que no lo desee picante.&#8221;<br /></p>', '<p>&nbsp;  &nbsp;  &nbsp;  &nbsp;  &nbsp;  &nbsp;  &nbsp; &#8220;Ingredientes para 3 porciones:</p>\n\n<p>Salsa</p>\n\n<p>4 jitomates<br />\n1 diente de ajo<br />\n1 taza de agua<br />\n1 cucharada de aceite<br />\n2 chiles poblano asados, pelados, desvenados y cortados en rajas<br />\n½ cebolla fileteada<br />\nsal al gusto</p>\n\n<p><br />\nHuevos</p>\n\n<p>6 huevos</p>\n\n<p><br />\nPresentación</p>\n\n<p>½ taza de queso panela cortado en cubos pequeños&#8221;<br /></p>', 5, 15, 2, 'saladas_14', 0, 3, 0),
(71, 'HUEVOS RANCHEROS', 10, 11, '<p>&nbsp;  &nbsp;  &nbsp;  &nbsp;  &nbsp;  &nbsp;  &nbsp;  &nbsp;  &nbsp;  &nbsp;  &nbsp;  &nbsp;  &nbsp;  &nbsp;  &nbsp;  &nbsp;  &nbsp;  &nbsp;  &nbsp;  &nbsp;  &#8220;Huevos</p>\n\n<p>• Caliente en un sartén el aceite y pase una a una las tortillas dentro de él, dejándolas suaves o crujientes según su preferencia. Colóquelas sobre papel absorbente para retirar el exceso de aceite.<br />\n• Haga unos huevos estrellados en el mismo sartén; si fuera necesario, añada más aceite entre cada uno.</p>\n\n<p><br />\nSalsa de chile de árbol</p>\n\n<p>• Cueza los jitomates, los chiles, la cebolla y los ajos en el agua. Retire del fuego, deje enfriar y lícuelos con un poco del agua de cocción.<br />\n• Caliente el aceite y fría la salsa. Deje reducir según la consistencia que desee, añada sal y reserve.</p>\n\n<p><br />\nPresentación</p>\n\n<p>• Coloque encima de cada tortilla un huevo y bañe con la salsa de chile de árbol. Acompañe con las rebanadas de bolillo.</p>\n\n<p>Generalmente el término ranchero es aplicado a las preparaciones que contienen alguna salsa o caldillo de jitomate.&#8221;<br /></p>', '<p>&nbsp;  &nbsp;  &nbsp;  &nbsp;  &nbsp;  &nbsp;  &nbsp;  &nbsp;  &nbsp;  &nbsp;  &nbsp;  &nbsp;  &nbsp;  &nbsp;  &nbsp;  &nbsp;  &nbsp;  &nbsp;  &nbsp;  &nbsp;  &#8220;Ingredientes para 3 porciones:</p>\n\n<p>Huevos</p>\n\n<p>6 tortillas de maíz<br />\nc/s de aceite para freír<br />\n6 huevos</p>\n\n<p><br />\nSalsa de chile de árbol</p>\n\n<p>½ kg de jitomates<br />\n2 chiles de árbol frescos grandes<br />\n¼ de cebolla<br />\n2 dientes de ajo chicos<br />\n1 l de agua<br />\n2 cucharadas de aceite<br />\nsal al gusto</p>\n\n<p><br />\nPresentación</p>\n\n<p>rebanadas de bolillo doradas al gusto&#8221;<br /></p>', 5, 15, 1, 'saladas_15', 0, 1, 0),
(72, 'HUEVOS TIRADOS', 10, 11, '<p>&nbsp;  &nbsp;  &nbsp;  &nbsp;  &nbsp;  &nbsp;  &nbsp; &#8220;Frijoles refritos</p>\r\n\r\n<p>• Machaque los frijoles con el caldo hasta que estén bien triturados.<br />\r\n• Caliente en un sartén la manteca a fuego alto, espere a que humee ligeramente y fría la cebolla hasta que esté muy dorada, casi negra. Añada los frijoles machacados y mueva constantemente, de lo contrario se pegarán al fondo; la preparación espesará en unos minutos. Después de un tiempo notará que los frijoles se habrán vuelto una masa que se mantiene unida; esa es la consistencia deseada. Reserve.</p>\r\n\r\n<p><br />\r\nSalsa de chile costeño</p>\r\n\r\n<p>• Cueza los jitomates y los chiles en el agua durante 10 minutos; retire del fuego y deje enfriar.<br />\r\n• Licue los ingredientes cocidos con el ajo y &frac13;  de taza del agua de cocción de los chiles hasta obtener una salsa tersa.<br />\r\n• Vierta la salsa en un recipiente, añada la cebolla, el cilantro y sal; mezcle y reserve.</p>\r\n\r\n<p><br />\r\nHuevos</p>\r\n\r\n<p>• Caliente el aceite, añada los huevos y sal. Mueva ocasionalmente, y cuando estén ligeramente cocidos, agregue los frijoles.<br />\r\n• Mezcle bien para integrar ambos ingredientes. Retire del fuego cuando todo esté cocido.</p>\r\n\r\n<p><br />\r\nPresentación</p>\r\n\r\n<p>• Sirva los huevos con la salsa de chile costeño, el queso fresco y las tortillas.</p>\r\n\r\n<p>Puede reservar algunos frijoles enteros y mezclarlos <br />\r\ncon los frijoles refritos.&#8221;<br /></p>', '<p>&nbsp;  &nbsp;  &nbsp;  &nbsp;  &nbsp;  &nbsp;  &nbsp; &#8220;Ingredientes para 3 porciones:</p>\n\n<p>Frijoles refritos (3 tazas)</p>\n\n<p>3 tazas de frijoles de la olla con su caldo (ver pág. 18)<br />\n1?3 de taza de manteca de cerdo<br />\n2 cucharadas de cebolla picada finamente</p>\n\n<p><br />\nSalsa de chile costeño</p>\n\n<p>250 g de jitomates asados<br />\n3 chiles costeños grandes sin semillas <br />\nni venas, asados<br />\n1 taza de agua<br />\n1 diente de ajo<br />\n¼ de taza de cebolla picada finamente<br />\n¼ de taza de cilantro picado finamente<br />\nsal al gusto</p>\n\n<p><br />\nHuevos</p>\n\n<p>3 cucharadas de aceite<br />\n6 huevos batidos ligeramente<br />\nsal al gusto<br />\n1 taza de frijoles refritos</p>\n\n<p><br />\nPresentación</p>\n\n<p>½ taza de queso fresco cortado en cubos (opcional)<br />\ntortillas de maíz al gusto&#8221;<br /></p>', 15, 25, 1, 'saladas_17', 0, 3, 0),
(73, 'Birria', 10, 12, '<p><strong>Birria</strong></p>\r\n<p>&bull; A&ntilde;ada sal a la carne y deje reposar por unos minutos.<br /> &bull; Licue los chiles con el resto de los ingredientes hasta obtener una salsa tersa y espesa. En caso de ser necesario, agregue un poco de agua; verifique la sal.<br /> &bull; Marine la carne con el molido de chiles durante una noche en refrigeraci&oacute;n.<br /> &bull; Al d&iacute;a siguiente, coloque una rejilla dentro de una olla de presi&oacute;n, a&ntilde;ada agua hasta casi tocarla, ponga encima de la rejilla un trozo de penca de maguey y acomode la carne. Cierre y cueza durante 1 hora a partir de que el vapor comience a escapar de la olla. Verifique la sal del jugo de carne que obtuvo de la cocci&oacute;n.</p>\r\n<p><br /> <strong>Salsa de chile cascabel</strong></p>\r\n<p>&bull; Licue todos los ingredientes hasta obtener una salsa tersa; verifique la sal.</p>\r\n<p><br /> <strong>Presentaci&oacute;n</strong></p>\r\n<p>&bull; Sirva la birria caliente y acompa&ntilde;e con la salsa, el or&eacute;gano, la cebolla, las tortillas de ma&iacute;z y los limones.</p>\r\n<p>&nbsp;</p>\r\n<p><em>Si lo desea, cuando la carne est&eacute; cocida deje hervir durante unos minutos en el jugo de carne.</em></p>', '<p>Ingredientes para 6 porciones:</p>\r\n<p>&nbsp;</p>\r\n<p><strong>Birria</strong></p>\r\n<p>1 kg de carne maciza de chivo cortada en trozos medianos<br />1/2 &nbsp;kg de chamorro o costilla de chivo<br /> sal al gusto<br /> 2 chiles ancho desvenados, sin semillas, asados y remojados<br /> 3 chiles guajillo desvenados, sin semillas, asados y remojados<br /> 2 chiles cascabel desvenados, sin semillas, asados y remojados<br /> 2 chiles mora desvenados, sin semillas, asados y remojados<br /> 4 dientes de ajo<br />1/2 &nbsp;cebolla<br />1/2 &nbsp;cucharadita de cominos<br /> 10 pimientas negras<br /> 2 clavos de olor<br /> 1 cucharadita de or&eacute;gano<br />1/2 &nbsp;taza de vinagre blanco</p>\r\n<p><br /><strong> Salsa de chile cascabel</strong></p>\r\n<p>250 g de jitomate asado, pelado y sin semillas<br /> 4 chiles cascabel desvenados, sin semillas, asados y remojados<br /> 1 taza del jugo de cocci&oacute;n de la carne, sin grasa<br /> sal al gusto</p>\r\n<p><br /><strong> Presentaci&oacute;n</strong></p>\r\n<p>or&eacute;gano al gusto<br /> cebolla picada al gusto<br /> tortillas de ma&iacute;z al gusto<br /> limones partidos al gusto</p>', 730, 90, 3, 'saladas_01', 0, 2, 0),
(74, 'Cecina', 10, 12, '<p><strong>Cecina</strong></p>\r\n<p>&bull; Ase la cecina en una parrilla con un poco de aceite.</p>\r\n<p><br /> <strong>Ensalada de nopales y habas</strong></p>\r\n<p>&bull; Mezcle todos los ingredientes y reserve.</p>\r\n<p><br /> <strong>Salsa de chile ancho</strong></p>\r\n<p>&bull; Hierva en un poco de agua los chiles ancho para hidratarlos; cuando est&eacute;n suaves, l&iacute;cuelos con los dem&aacute;s ingredientes y reserve.</p>\r\n<p><br /> <strong>Presentaci&oacute;n</strong></p>\r\n<p>&bull; Sirva la cecina acompa&ntilde;ada del queso, las cebollas cambray, el aguacate, la ensalada de nopales y habas y la salsa de chile ancho.</p>', '<p>Ingredientes para 6 porciones:</p>\r\n<p>&nbsp;</p>\r\n<p><strong>Cecina</strong></p>\r\n<p>1 kg de cecina<br /> c/s de aceite</p>\r\n<p><br /> <strong>Ensalada de nopales y habas</strong></p>\r\n<p>6 nopales cocidos en agua con sal y cortados en cubos<br /> 300 g de habas frescas cocidas, sin c&aacute;scara<br /> 3 jitomates pelados, sin semillas y cortados en cubos peque&ntilde;os<br /> 1 cebolla picada finamente<br />1/2 taza de cilantro picado<br /> 4 cucharadas de aceite de oliva<br /> 4 cucharadas de vinagre blanco<br /> 2 cucharadas de or&eacute;gano<br /> queso fresco desmoronado al gusto (opcional)<br /> sal al gusto</p>\r\n<p><br /> <strong>Salsa de chile ancho</strong></p>\r\n<p>6 chiles ancho sin semillas ni venas<br /> 1 jitomate asado<br /> 2 dientes de ajo asados<br /> 1 cucharadita de or&eacute;gano<br />1/4 de raja de canela asada<br /> 3 cucharadas de vinagre de manzana<br /> sal al gusto</p>\r\n<p><br /> <strong>Presentaci&oacute;n</strong></p>\r\n<p>300 g de queso fresco cortado en rebanadas delgadas (opcional)<br /> 18 cebollas cambray asadas<br /> 1 aguacate cortado en rebanadas (opcional)</p>', 20, 15, 2, 'saladas_02', 0, 1, 0),
(75, 'Chicharrón en salsa verde', 10, 12, '<p><strong>Salsa verde</strong></p>\r\n<p>&bull; Ase a fuego medio los chiles, los tomates, la cebolla y el ajo.<br /> &bull; Pele el ajo y licue los ingredientes asados con el cilantro y sal. Si es necesario,&nbsp;agregue un poco de caldo de pollo para obtener una consistencia m&aacute;s ligera.<br /> &bull; Caliente la manteca en una cacerola a fuego alto, vierta la salsa y cocine por 5 minutos moviendo de vez en cuando para que no se pegue.</p>\r\n<p><br /> <strong>Presentaci&oacute;n</strong></p>\r\n<p>&bull; A&ntilde;ada el chicharr&oacute;n a la salsa y cueza hasta que &eacute;ste se ablande; verifique la&nbsp;sal.<br /> &bull; Sirva caliente acompa&ntilde;ado con los frijoles de la olla y las tortillas de ma&iacute;z.</p>\r\n<p>&nbsp;</p>\r\n<p><em>&Eacute;ste es un platillo para desayunar muy acostumbrado en la zona centro del pa&iacute;s. Se consume por igual en la ma&ntilde;ana o al mediod&iacute;a.</em></p>', '<p>Ingredientes para 6 porciones:</p>\r\n<p>&nbsp;</p>\r\n<p><strong>Salsa verde</strong></p>\r\n<p>4 chiles serrano verdes<br /> 750 g de tomates troceados<br /> 1 cebolla<br /> 1 diente de ajo grande con piel<br />1/2 taza de cilantro troceado<br /> sal al gusto<br /> c/s de caldo de pollo<br /> 3 cucharadas de manteca de cerdo</p>\r\n<p><br /> <strong>Presentaci&oacute;n</strong></p>\r\n<p>350 g de chicharr&oacute;n de cerdo cortado en trozos<br /> frijoles de la olla al gusto<br /> tortillas de ma&iacute;z al gusto</p>', 10, 15, 1, 'saladas_03', 0, 1, 0),
(76, 'Chilaquiles colimotes', 10, 12, '<p><strong>Salsa roja</strong></p>\r\n<p>&bull; Cueza los jitomates con los chiles y el agua; retire del fuego y deje enfriar.<br /> &bull; Licue los jitomates con el agua de cocci&oacute;n, los chiles, la cebolla y los ajos hasta obtener una salsa tersa y fina.<br /> &bull; Caliente el aceite en una olla y vierta la salsa. Deje cocer durante algunos minutos y agregue sal. Reserve caliente.</p>\r\n<p><br /> <strong>Chilaquiles</strong></p>\r\n<p>&bull; Fr&iacute;a los tri&aacute;ngulos de tortilla en abundante aceite caliente. Cuando est&eacute;n ligeramente dorados, retire el aceite y vierta los huevos batidos; mezcle hasta que est&eacute;n cocidos.<br /> &bull; A&ntilde;ada la salsa y deje cocinar por unos minutos m&aacute;s; verifique la sal y retire del fuego.</p>\r\n<p><br /> <strong>Presentaci&oacute;n</strong></p>\r\n<p>&bull; Sirva los chilaquiles acompa&ntilde;ados del queso a&ntilde;ejo, la crema y la cebolla.</p>\r\n<p>&nbsp;</p>\r\n<p><em>En un cl&aacute;sico desayuno colimense, no pueden faltar estos chilaquiles, un tamal de ceniza y un birote. Todo puede disfrutarse mejor con un vaso de tuba.</em></p>', '<p>Ingredientes para 4 porciones:</p>\r\n<p>&nbsp;</p>\r\n<p><strong>Salsa roja</strong></p>\r\n<p>1/2 kg de jitomates<br /> 2 chiles jalape&ntilde;o<br /> 2 tazas de agua<br />1/2 cebolla<br /> 2 dientes de ajo<br /> 2 cucharadas de aceite<br /> sal al gusto</p>\r\n<p><br /> <strong>Chilaquiles</strong></p>\r\n<p>12 tortillas de ma&iacute;z cortadas en tri&aacute;ngulos peque&ntilde;os<br /> c/s de aceite para fre&iacute;r<br /> 6 huevos batidos ligeramente<br /> sal al gusto</p>\r\n<p><br /> <strong>Presentaci&oacute;n</strong></p>\r\n<p>1/2 taza de queso a&ntilde;ejo rallado<br />1/2 taza de crema<br />1/2 cebolla fileteada</p>', 10, 15, 1, 'saladas_04', 0, 2, 0),
(77, 'Chilaquiles verdes con pollo', 10, 12, '<p><strong>Salsa</strong></p>\r\n<p>&bull; Cueza los tomates con los chiles y el caldo de pollo. Retire del fuego, deje enfriar y l&iacute;cuelos con la cebolla, los ajos y el cilantro hasta obtener una salsa tersa.<br /> &bull; Caliente el aceite en una olla y fr&iacute;a la salsa. Pruebe y ajuste la cantidad de sal. En el &uacute;ltimo hervor a&ntilde;ada la rama de epazote.</p>\r\n<p><br /> <strong>Chilaquiles</strong></p>\r\n<p>&bull; Sumerja en la salsa los tri&aacute;ngulos de tortilla fritos; deje hervir un par de minutos y retire del fuego.</p>\r\n<p><br /> <strong>Presentaci&oacute;n</strong></p>\r\n<p>&bull; Sirva inmediatamente acompa&ntilde;ados con el pollo deshebrado, el queso</p>\r\n<p>fres&shy;co, la crema y la cebolla.</p>\r\n<p>&nbsp;</p>\r\n<p><em>Si desea, sustituya el pollo por huevo. Grat&iacute;nelos con queso Chihuahua o manchego antes de servir.</em></p>', '<p>Ingredientes para 6 porciones:</p>\r\n<p>&nbsp;</p>\r\n<p><strong>Salsa</strong></p>\r\n<p>1/2 kg de tomates<br /> 6 chiles serrano verdes<br /> 2 tazas de caldo de pollo<br />1/2 cebolla<br /> 2 dientes de ajo<br />1/2 taza de cilantro troceado<br /> 3 cucharadas de aceite<br /> sal al gusto<br /> 1 rama de epazote</p>\r\n<p><br /> <strong>Chilaquiles</strong></p>\r\n<p>1/2 kg de tortillas de ma&iacute;z cortadas en tri&aacute;ngulos y fritas</p>\r\n<p><br /> <strong>Presentaci&oacute;n</strong></p>\r\n<p>1 pechuga de pollo cocida y deshebrada<br /> 1 taza de queso fresco rallado<br />1/2 taza de crema (opcional)<br />1/2 cebolla cortada fileteada</p>', 10, 15, 2, 'saladas_05', 0, 1, 0),
(78, 'Enfrijoladas', 10, 12, '<p><strong>Frijoles de la olla</strong></p>\r\n<p>&bull; Coloque los frijoles, la cebolla, el diente de ajo y 1 litro de agua dentro de una olla de presi&oacute;n, cierre y ponga sobre el fuego. Cuente de 30 a 35 minutos de cocci&oacute;n a partir de que empiece a escapar el vapor de la olla.<br /> &bull; Retire del fuego, deje enfriar por completo la olla, dest&aacute;pela y a&ntilde;ada la manteca, el epazote, sal, y si lo desea, el agua restante. Ponga a hervir nuevamente para que se integren los sabores. Reserve.</p>\r\n<p><br /> <strong>Enfrijoladas</strong></p>\r\n<p>&bull; Licue los frijoles de la olla con un poco de su caldo y el chile chipotle. Ca&shy;liente una cacerola con la manteca y cuando humee vierta los frijoles molidos. A&ntilde;ada la rama de epazote y deje hervir durante 5 minutos. Retire el epazote y reserve. Al final deber&aacute; obtener una salsa ligeramente espesa.<br /> &bull; Caliente el aceite y pase dentro de &eacute;l las tortillas una a una por unos segundos, s&oacute;lo para que se suavicen.</p>\r\n<p><br /> <strong>Presentaci&oacute;n</strong></p>\r\n<p>&bull; Acomode 3 tortillas en un plato, rell&eacute;nelas con el pollo y enr&oacute;llelas.<br /> &bull; Ba&ntilde;e con los frijoles molidos y acompa&ntilde;e con la crema, el queso y la ce&shy;bolla.</p>', '<p>Ingredientes para 4 porciones:</p>\r\n<p>&nbsp;</p>\r\n<p><strong>Frijoles de la olla</strong></p>\r\n<p>250 g de frijoles limpios, remojados desde la noche anterior y drenados<br />1/2 cebolla<br /> 1 diente de ajo<br /> 1 1/2 litros&nbsp;de agua<br /> 1 1/2 cucharadas de manteca de cerdo<br /> 1 rama de epazote<br /> sal al gusto</p>\r\n<p><br /><strong> Enfrijoladas</strong></p>\r\n<p>1 chile chipotle en escabeche<br /> 2 cucharadas de manteca de cerdo<br /> 1 rama de epazote<br /> c/s de aceite para fre&iacute;r<br /> 12 tortillas de ma&iacute;z</p>\r\n<p><br /><strong> Presentaci&oacute;n</strong></p>\r\n<p>1/2 pechuga de pollo cocida y deshebrada<br /> 1 taza de crema<br /> 150 g de queso fresco<br />1/2 cebolla picada finamente</p>', 15, 45, 2, 'saladas_06', 0, 2, 0),
(79, 'Enmoladas', 10, 12, '<p><strong>Enmoladas</strong></p>\r\n<p>&bull; Caliente el caldo de pollo y disuelva el mole hasta que obtenga una consistencia no muy espesa. Agregue sal si fuera necesario.<br /> &bull; Caliente el aceite y pase dentro de &eacute;l las tortillas una a una por unos segundos s&oacute;lo para que se suavicen.</p>\r\n<p><br /> <strong>Presentaci&oacute;n</strong></p>\r\n<p>&bull; Introduzca una a una las tortillas en el mole, rell&eacute;nelas con la pechuga de pollo y d&oacute;blelas a la mitad. Sirva tres enmoladas por plato y termine ba&ntilde;&aacute;ndolas con mole. Agregue la crema y el queso; acompa&ntilde;e con los frijoles bayos refritos.</p>\r\n<p><br /> <em>Esta preparaci&oacute;n forma parte de la gran familia de enchiladas del pa&iacute;s. Son tradicionales las elaboradas con mole poblano, pero pueden prepararse con cualquier otro tipo de mole: negro, almendrado o incluso de Xico.</em></p>', '<p>Ingredientes para 4 porciones:</p>\r\n<p>&nbsp;</p>\r\n<p><strong>Enmoladas</strong></p>\r\n<p>750 ml de caldo de pollo<br /> 300 g de mole en pasta<br /> sal al gusto<br /> c/s de aceite para fre&iacute;r<br /> 12 tortillas de ma&iacute;z</p>\r\n<p><br /> <strong>Presentaci&oacute;n</strong></p>\r\n<p>1 pechuga de pollo cocida y deshebrada<br />1/2 taza de crema<br /> 50 g de queso fresco desmenuzado<br /> frijoles bayos refritos al gusto</p>', 10, 15, 2, 'saladas_07', 0, 1, 0),
(80, 'Huarache con costillitas', 10, 12, '<p><strong>Huarache</strong></p>\r\n<p>&bull; Mezcle la masa con un poco de agua y sal.<br /> &bull; Elabore bolas con la masa y deles forma alargada. Apl&aacute;nelas entre 2 hojas de pl&aacute;stico hasta que tengan 1/2 cent&iacute;metro de grosor.<br /> &bull; Fr&iacute;a los huaraches en suficiente aceite.</p>\r\n<p><br /><strong> Presentaci&oacute;n</strong></p>\r\n<p>&bull; Unte a cada huarache frijoles refritos, agregue salsa verde, cebolla, queso y acompa&ntilde;e con un trozo de costillita. S&iacute;rvalos inmediatamente.</p>\r\n<p>&nbsp;</p>\r\n<p><em>Este desayuno es cl&aacute;sico para los domingos. En los mercados populares y en los establecimientos donde venden quesadillas, se llegan a ofrecer huaraches de proporciones enormes con un bistec de costilla de igual tama&ntilde;o o un huevo frito.</em></p>', '<p>Ingredientes para 4 porciones:</p>\r\n<p>&nbsp;</p>\r\n<p><strong>Huarache</strong></p>\r\n<p>300 g de masa para tortillas<br /> c/s de agua<br /> sal al gusto<br /> c/s de aceite para fre&iacute;r</p>\r\n<p><br /> <strong>Presentaci&oacute;n</strong></p>\r\n<p>1 taza de frijoles refritos<br /> 1 taza de salsa verde cruda<br />1/2 cebolla picada finamente<br /> 1 taza de queso rallado<br />1/2 kg de costillitas de cerdo cocidas y fritas</p>', 15, 10, 2, 'saladas_08', 0, 2, 0),
(81, 'Huevos a la albañil', 10, 12, '<p><strong>Huevos</strong></p>\r\n<p>&bull; Caliente un sart&eacute;n con el aceite, agregue sal a los huevos y h&aacute;galos revueltos; ret&iacute;relos del sart&eacute;n y reserve.</p>\r\n<p><br /> <strong>Salsa</strong></p>\r\n<p>&bull; Licue los jitomates con la cebolla, el diente de ajo y los chiles de &aacute;rbol. Caliente el aceite en el mismo sart&eacute;n donde prepar&oacute; los huevos y vierta la salsa; cueza durante 2 minutos y verifique la sal.<br /> &bull; Agregue los huevos y deje hervir durante 2 minutos m&aacute;s.</p>\r\n<p><br /> <strong>Presentaci&oacute;n</strong></p>\r\n<p>&bull; Sirva acompa&ntilde;ados con los totopos, los frijoles refritos y el queso.</p>\r\n<p><br /> <em>El adjetivo &ldquo;a la alba&ntilde;il&rdquo; indica la personalidad picante de este desayuno. Para esta receta se ha reducido la cantidad de chiles de &aacute;rbol, pero, si lo desea, es posible hacer honor al nombre agregando m&aacute;s.</em></p>', '<p>Ingredientes para 3 porciones:</p>\r\n<p>&nbsp;</p>\r\n<p><strong>Huevos</strong></p>\r\n<p>3 cucharadas de aceite<br /> 6 huevos batidos ligeramente<br /> sal al gusto</p>\r\n<p><br /> <strong>Salsa</strong></p>\r\n<p>2 jitomates asados<br />1/4 de cebolla asada<br /> 1 diente de ajo asado<br /> 2 chiles de &aacute;rbol secos asados<br /> 2 cucharadas de aceite<br /> sal al gusto</p>\r\n<p><br /> <strong>Presentaci&oacute;n</strong></p>\r\n<p>totopos al gusto<br /> frijoles refritos al gusto<br /> queso fresco desmoronado, al gusto</p>', 5, 10, 1, 'saladas_09', 0, 1, 0),
(82, 'Huevos a la veracruzana', 10, 12, '<p><strong>Relleno</strong></p>\r\n<p>&bull; Caliente en un sart&eacute;n el aceite y acitrone la cebolla. A&ntilde;ada los jitomates, el chile jalape&ntilde;o y sofr&iacute;a durante 3 minutos. Agregue los huevos y la sal; mezcle y retire del fuego cuando todo est&eacute; cocido. Reserve.</p>\r\n<p><br /><strong> Preparaci&oacute;n</strong></p>\r\n<p>&bull; Caliente el aceite y pase dentro de &eacute;l las tortillas una a una.<br /> &bull; Disponga 3 tortillas en un plato y rellene cada una con el huevo guisado. Acompa&ntilde;e con los frijoles y adorne con el chorizo, el queso y las rajas.</p>\r\n<p><br /> <em>Tradicionalmente se elabora una salsa con los frijoles y se ba&ntilde;an las tortillas rellenas</em></p>', '<p>Ingredientes para 4 porciones:</p>\r\n<p>&nbsp;</p>\r\n<p><strong>Relleno</strong></p>\r\n<p>4 cucharadas de aceite<br />1/2 cebolla picada finamente<br /> 2 jitomates picados<br /> 1 chile jalape&ntilde;o sin semillas ni venas, picado finamente<br /> 8 huevos batidos ligeramente<br />1/2 cucharadita de sal</p>\r\n<p><br /> <strong> Presentaci&oacute;n</strong></p>\r\n<p>12 tortillas<br /> c/s de aceite para fre&iacute;r<br /> 3 tazas de frijoles de la olla<br /> 5 cucharadas de chorizo frito<br /> 100 g de queso fresco cortado en cubos<br /> 1 chile cuaresme&ntilde;o sin venas ni semillas, cortado en rajas y fritas</p>', 5, 10, 2, 'saladas_10', 0, 2, 0),
(83, 'Huevos ahogados', 10, 12, '<p><strong>Salsa</strong></p>\r\n<p>&bull; Licue los jitomates, la cebolla, los chiles guajillo, los dientes de ajo, el caldo de pollo y sal.<br /> &bull; Caliente el aceite en una cacerola extendida y vierta la salsa; cueza durante</p>\r\n<p>5 minutos y verifique la sal.</p>\r\n<p><br /> <strong>Huevos</strong></p>\r\n<p>&bull; Baje el fuego al m&iacute;nimo y vierta con cuidado los huevos uno a uno a la salsa, procurando que queden enteros y separados entre ellos. Unos minutos antes de que los huevos se cuezan, agregue los nopales, procurando no romper las yemas.<br /> &bull; Retire del fuego y sirva.</p>\r\n<p><br /> <em>El curioso nombre de este platillo se debe a que los huevos &ldquo;nadan&rdquo; en un caldillo de jitomate.</em></p>', '<p>Ingredientes para 3 porciones:</p>\r\n<p>&nbsp;</p>\r\n<p><strong>Salsa</strong></p>\r\n<p>6 jitomates asados<br />1/2 cebolla asada<br /> 3 chiles guajillo desvenados, asados y remojados (opcional)<br /> 2 dientes de ajo asados<br /> 1 litro&nbsp;de caldo de pollo<br /> sal al gusto<br /> c/s de aceite</p>\r\n<p><br /> <strong>Huevos</strong></p>\r\n<p>6 huevos<br /> 1 1/2 tazas de nopales cortados en tiras y cocidos</p>', 5, 15, 1, 'saladas_11', 0, 2, 0),
(84, 'Huevos aporreados', 10, 12, '<p><strong>Huevos</strong></p>\r\n<p>&bull; Caliente en un sart&eacute;n 1 cucharada de aceite y sofr&iacute;a la carne seca o cecina. Cuando est&eacute; dorada, retire del fuego y c&oacute;rtela en cuadros peque&ntilde;os; reserve.<br /> &bull; Caliente el aceite restante en el mismo sart&eacute;n y acitrone la cebolla, el ajo, los jitomates y los chiles. A&ntilde;ada la cecina, los huevos y sal; mezcle y retire del fuego cuando todo est&eacute; cocido.</p>\r\n<p><br /> <strong>Salsa de chile cascabel</strong></p>\r\n<p>&bull; Ase a fuego bajo los chiles por el lado brillante. Ret&iacute;relos del fuego y deje enfriar.<br /> &bull; Coloque los jitomates asados en una olla peque&ntilde;a, c&uacute;bralos con el agua y cue&shy;za por 15 minutos. Deje enfriar y l&iacute;cuelos con los chiles, los ajos, la sal y&nbsp;el agua hasta obtener una salsa martajada.<br /> &bull; Si lo desea, caliente un poco de aceite y fr&iacute;a la salsa por 10 minutos.</p>\r\n<p><br /> <strong>Presentaci&oacute;n</strong></p>\r\n<p>&bull; Sirva los huevos acompa&ntilde;ados de la salsa de chile cascabel y las tortillas de ma&iacute;z.</p>\r\n<p><br /> <em> Esta preparaci&oacute;n, t&iacute;pica de Tierra Caliente en Michoac&aacute;n y Guerrero, tambi&eacute;n puede llamarse aporreada o aporreado. La peculiaridad consiste en la utilizaci&oacute;n de carne seca, la cual se corta o desmenuza y luego se golpea para abrir las fibras de la carne antes de utilizarla.</em></p>', '<p>Ingredientes para 4 porciones:</p>\r\n<p>&nbsp;</p>\r\n<p><strong>Huevos</strong></p>\r\n<p>250 g de carne seca o cecina<br /> 3 cucharadas de aceite<br /> 1 cebolla picada finamente<br /> 1 diente de ajo picado finamente<br /> 3 jitomates picados<br /> 2 chiles serranos sin venas ni semillas picados finamente<br /> 8 huevos batidos ligeramente<br /> sal al gusto</p>\r\n<p><br /> <strong>Salsa de chile cascabel</strong></p>\r\n<p>6 chiles cascabel limpios sin semillas ni venas<br /> 2 jitomates asados<br />3/4 de taza de agua<br /> 2 dientes de ajo grandes asados, pelados y troceados<br /> 1 cucharadita de sal</p>\r\n<p><br /> <strong>Presentaci&oacute;n</strong></p>\r\n<p>Tortillas de ma&iacute;z al gusto</p>', 10, 25, 2, 'saladas_12', 0, 2, 0),
(85, 'Huevos divorciados', 10, 12, '<p><strong>Huevos</strong></p>\r\n<p>&bull; Caliente aceite en un sart&eacute;n y haga un huevo estrellado; a&ntilde;ada un poco de sal durante la cocci&oacute;n. Contin&uacute;e con el resto de los huevos a&ntilde;adiendo m&aacute;s aceite entre cada uno.</p>\r\n<p><br /> <strong>Presentaci&oacute;n</strong></p>\r\n<p>&bull; Ponga 2 huevos estrellados por plato; ba&ntilde;e uno con salsa verde y otro con salsa roja, y acompa&ntilde;e con los frijoles refritos y los totopos.</p>\r\n<p><br /> <em>&Eacute;sta es una de las formas m&aacute;s populares de consumir el huevo en M&eacute;xico. Para hacer m&aacute;s evidente el nombre puede servir los huevos separados con un poco de frijoles.</em></p>', '<p>Ingredientes para 4 porciones:</p>\r\n<p>&nbsp;</p>\r\n<p><strong>Huevos</strong></p>\r\n<p>8 huevos<br /> c/s de aceite para fre&iacute;r<br /> sal al gusto</p>\r\n<p><br /> <strong>Presentaci&oacute;n</strong></p>\r\n<p>1 receta de salsa verde<br /> 1 receta de salsa roja<br /> 1 1/2 tazas de frijoles refritos<br /> totopos al gusto</p>', 5, 15, 1, 'saladas_13', 0, 2, 0),
(86, 'Huevos motuleños', 10, 12, '<p><strong>Huevos</strong></p>\r\n<p>&bull; Caliente el aceite en un sart&eacute;n y pase una a una las tortillas dentro de &eacute;l s&oacute;lo para que se suavicen. Col&oacute;quelas sobre papel absorbente para eliminar el exceso de grasa y reserve.<br /> &bull; Haga unos huevos estrellados en el mismo sart&eacute;n, agregue sal. Si fuera necesario, a&ntilde;ada m&aacute;s aceite entre cada huevo.<br /> &bull; Unte sobre cada tortilla un poco de frijoles refritos y ponga encima 1 huevo.</p>\r\n<p><br /> <strong>Salsa</strong></p>\r\n<p>&bull; Licue los jitomates, la cebolla, el ajo, el vinagre y el agua.<br /> &bull; Caliente en una cacerola el aceite y vierta la salsa. A&ntilde;ada la hoja de laurel, el chile habanero y sal; deje hervir a fuego bajo durante 5 minutos sin dejar de mover. Retire del fuego, deseche la hoja de laurel, verifique la sal y reserve.</p>\r\n<p><br /> <strong>Presentaci&oacute;n</strong></p>\r\n<p>&bull; Acompa&ntilde;e los huevos con la salsa y las guarniciones.</p>\r\n<p>&nbsp;</p>\r\n<p><em>Tradicionalmente estos huevos se ba&ntilde;an con la salsa, pero puede servirla en un recipiente por separado.</em></p>', '<p>Ingredientes para 3 porciones:</p>\r\n<p>&nbsp;</p>\r\n<p><strong>Huevos</strong></p>\r\n<p>6 tortillas de ma&iacute;z<br />1/2 taza de aceite<br /> 6 huevos<br /> 1 taza de frijoles refritos<br /> sal al gusto</p>\r\n<p><br /> <strong>Salsa</strong></p>\r\n<p>2 jitomates<br />1/4 de cebolla<br /> 1 diente de ajo<br /> 1 cucharadita de vinagre<br /> 1 taza de agua<br /> 2 cucharadas de aceite<br /> 1 hoja de laurel<br /> 1 chile habanero<br /> sal al gusto</p>\r\n<p><br /> <strong>Guarniciones</strong></p>\r\n<p>100 g de jam&oacute;n cortado en cubos peque&ntilde;os<br /> 100 g de ch&iacute;charos cocidos<br /> 100 g de queso fresco rallado<br /> 1 pl&aacute;tano macho cortado en rebanadas y fritas</p>', 5, 15, 2, 'saladas_14', 0, 2, 0),
(87, 'Huevos rabo de mestiza', 10, 12, '<p><strong>Salsa</strong></p>\r\n<p>&bull; Licue los jitomates con el ajo y el agua. Caliente el aceite en una cacerola extendida y fr&iacute;a el molido; deje cocinar durante unos minutos.<br /> &bull; Agregue las rajas de chile, la cebolla y sal; cueza durante 3 minutos.</p>\r\n<p><br /> <strong>Huevos</strong></p>\r\n<p>&bull; A&ntilde;ada los huevos, uno a uno, evitando que se junten y que se estrellen las yemas. Deje hervir hasta que est&eacute;n cocidos.</p>\r\n<p><br /> <strong>Presentaci&oacute;n</strong></p>\r\n<p>&bull; Sirva acompa&ntilde;ados con los cubos de queso.</p>\r\n<p><br /> <em>Tradicionalmente este platillo se elabora con rajas de chile poblano, pero puede sustituirlas por rajas de pimiento morr&oacute;n en caso de que no lo desee picante.</em></p>', '<p>Ingredientes para 3 porciones:</p>\r\n<p>&nbsp;</p>\r\n<p><strong>Salsa</strong></p>\r\n<p>4 jitomates<br /> 1 diente de ajo<br /> 1 taza de agua<br /> 1 cucharada de aceite<br /> 2 chiles poblano asados, pelados, desvenados y cortados en rajas<br />1/2 cebolla fileteada<br /> sal al gusto</p>\r\n<p><br /> <strong>Huevos</strong></p>\r\n<p>6 huevos</p>\r\n<p><br /> <strong>Presentaci&oacute;n</strong></p>\r\n<p>&frac12; taza de queso panela cortado en cubos peque&ntilde;os</p>', 5, 15, 1, 'saladas_15', 0, 2, 0);
INSERT INTO `recetas` (`id`, `titulo`, `id_categoria`, `id_app`, `procedimiento`, `ingredientes`, `preparacion`, `coccion`, `costo`, `foto`, `user_fav`, `dificultad`, `preparada`) VALUES
(88, 'Huevos rancheros', 10, 12, '<p><strong>Huevos</strong></p>\r\n<p>&bull; Caliente en un sart&eacute;n el aceite y pase una a una las tortillas dentro de &eacute;l, dej&aacute;ndolas suaves o crujientes seg&uacute;n su preferencia. Col&oacute;quelas sobre papel absorbente para retirar el exceso de aceite.<br /> &bull; Haga unos huevos estrellados en el mismo sart&eacute;n; si fuera necesario, a&ntilde;ada m&aacute;s aceite entre cada uno.</p>\r\n<p><br /> <strong>Salsa de chile de &aacute;rbol</strong></p>\r\n<p>&bull; Cueza los jitomates, los chiles, la cebolla y los ajos en el agua. Retire del fuego, deje enfriar y l&iacute;cuelos con un poco del agua de cocci&oacute;n.<br /> &bull; Caliente el aceite y fr&iacute;a la salsa. Deje reducir seg&uacute;n la consistencia que desee, a&ntilde;ada sal y reserve.</p>\r\n<p><br /> <strong>Presentaci&oacute;n</strong></p>\r\n<p>&bull; Coloque encima de cada tortilla un huevo y ba&ntilde;e con la salsa de chile de &aacute;rbol. Acompa&ntilde;e con las rebanadas de bolillo.</p>\r\n<p><br /> <em> Generalmente el t&eacute;rmino ranchero es aplicado a las preparaciones que contienen alguna salsa o caldillo de jitomate. </em></p>', '<p>Ingredientes para 3 porciones:</p>\r\n<p>&nbsp;</p>\r\n<p><strong>Huevos</strong></p>\r\n<p>6 tortillas de ma&iacute;z<br /> c/s de aceite para fre&iacute;r<br /> 6 huevos</p>\r\n<p><br /> <strong>Salsa de chile de &aacute;rbol</strong></p>\r\n<p>1/2 kg de jitomates<br /> 2 chiles de &aacute;rbol frescos grandes<br />1/4 de cebolla<br /> 2 dientes de ajo chicos<br /> 1 litro&nbsp;de agua<br /> 2 cucharadas de aceite<br /> sal al gusto</p>\r\n<p><br /> <strong>Presentaci&oacute;n</strong></p>\r\n<p>rebanadas de bolillo doradas al gusto</p>', 5, 15, 1, 'saladas_16', 0, 1, 0),
(89, 'Huevos tirados', 10, 12, '<p><strong>Frijoles refritos</strong></p>\r\n<p>&bull; Machaque los frijoles con el caldo hasta que est&eacute;n bien triturados.<br /> &bull; Caliente en un sart&eacute;n la manteca a fuego alto, espere a que humee ligeramente y fr&iacute;a la cebolla hasta que est&eacute; muy dorada, casi negra. A&ntilde;ada los frijoles machacados y mueva constantemente, de lo contrario se pegar&aacute;n al fondo; la preparaci&oacute;n espesar&aacute; en unos minutos. Despu&eacute;s de un tiempo notar&aacute; que los frijoles se habr&aacute;n vuelto una masa que se mantiene unida; esa es la consistencia deseada. Reserve.</p>\r\n<p><br /> <strong> Salsa de chile coste&ntilde;o</strong></p>\r\n<p>&bull; Cueza los jitomates y los chiles en el agua durante 10 minutos; retire del fuego y deje enfriar.<br /> &bull; Licue los ingredientes cocidos con el ajo y 1/3 de taza del agua de cocci&oacute;n de los chiles hasta obtener una salsa tersa.<br /> &bull; Vierta la salsa en un recipiente, a&ntilde;ada la cebolla, el cilantro y sal; mezcle y reserve.</p>\r\n<p><br /> <strong> Huevos</strong></p>\r\n<p>&bull; Caliente el aceite, a&ntilde;ada los huevos y sal. Mueva ocasionalmente, y cuando est&eacute;n ligeramente cocidos, agregue los frijoles.<br /> &bull; Mezcle bien para integrar ambos ingredientes. Retire del fuego cuando todo est&eacute; cocido.</p>\r\n<p><br /> <strong> Presentaci&oacute;n</strong></p>\r\n<p>&bull; Sirva los huevos con la salsa de chile coste&ntilde;o, el queso fresco y las tortillas.</p>\r\n<p>&nbsp;</p>\r\n<p><em> Puede reservar algunos frijoles enteros y mezclarlos con los frijoles refritos.</em></p>', '<p>Ingredientes para 3 porciones:</p>\r\n<p>&nbsp;</p>\r\n<p><strong>Frijoles refritos (3 tazas)</strong></p>\r\n<p>3 tazas de frijoles de la olla con su caldo <br /> 1/3 de taza de manteca de cerdo<br /> 2 cucharadas de cebolla picada finamente</p>\r\n<p><br /> <strong>Salsa de chile coste&ntilde;o</strong></p>\r\n<p>250 g de jitomates asados<br /> 3 chiles coste&ntilde;os grandes sin semillas ni venas, asados<br /> 1 taza de agua<br /> 1 diente de ajo<br />1/4 de taza de cebolla picada finamente<br />1/4 de taza de cilantro picado finamente<br /> sal al gusto</p>\r\n<p><br /> <strong>Huevos</strong></p>\r\n<p>3 cucharadas de aceite<br /> 6 huevos batidos ligeramente<br /> sal al gusto<br /> 1 taza de frijoles refritos</p>\r\n<p><br /> <strong>Presentaci&oacute;n</strong></p>\r\n<p>1/2 taza de queso fresco cortado en cubos (opcional) tortillas de ma&iacute;z al gusto</p>', 15, 25, 1, 'saladas_17', 0, 1, 0),
(90, 'Machaca con huevo', 10, 12, '<p><strong>Salsa</strong></p>\r\n<p>&bull; Cueza los jitomates, los chiles, la cebolla y los ajos con el agua y sal. Retire del fuego, deje enfriar, licue, cuele y reserve.</p>\r\n<p><br /> <strong>Machaca</strong></p>\r\n<p>&bull; Caliente el aceite en un sart&eacute;n, agregue la machaca y fr&iacute;a ligeramente.<br /> &bull; A&ntilde;ada los huevos, deje cocer un poco y vierta la salsa. Mezcle y mueva ocasionalmente hasta que todo est&eacute; cocido.</p>\r\n<p><br /> <strong>Frijoles charros</strong></p>\r\n<p>&bull; Cueza los frijoles con el agua hasta que est&eacute;n suaves; en olla de presi&oacute;n 30 minutos y en olla normal, 2&nbsp;horas.<br /> &bull; Fr&iacute;a el tocino en una cazuela a fuego medio hasta que se dore ligeramente y suelte su grasa; agregue el chorizo, deje dorar un poco y a&ntilde;ada la cebolla, el ajo, los jitomates, el chile y sal; deje que todo se sofr&iacute;a muy bien.<br /> &bull; A&ntilde;ada el sofrito de tocino cuando los frijoles est&eacute;n cocidos y suaves, y deje cocer durante 10 minutos m&aacute;s.<br /> &bull; Pruebe, ajuste la cantidad de sal y agregue el cilantro y el chicharr&oacute;n poco antes de servir los frijoles.</p>\r\n<p><br /> <strong>Presentaci&oacute;n</strong></p>\r\n<p>&bull; Acompa&ntilde;e con los frijoles charros, las tortillas de harina, los jitomates y el chile de &aacute;rbol.</p>', '<p>Ingredientes para 3 porciones:</p>\r\n<p>&nbsp;</p>\r\n<p><strong>Salsa (opcional)</strong></p>\r\n<p>1/2 kg de jitomates<br /> 3 chiles de &aacute;rbol frescos<br />1/2 cebolla<br /> 2 dientes de ajo<br /> 1 litro&nbsp;de agua<br /> sal al gusto</p>\r\n<p><br /> <strong>Machaca</strong></p>\r\n<p>3 cucharadas de aceite<br /> 300 g de machaca (carne seca picada)<br /> 6 huevos batidos ligeramente</p>\r\n<p><br /> <strong>Frijoles charros</strong></p>\r\n<p>125 g de frijoles bayos remojados desde la noche anterior<br /> 1 1/2 litros&nbsp;de agua<br /> 50 g de tocino picado<br /> 75 g de chorizo desmenuzado<br />1/2 cebolla picada<br />1/2 cucharada de ajo picado finamente<br /> 1 1/2 jitomates picados<br /> 1 1/2 cucharadas de chile serrano picado finamente<br />1/4 de taza de cilantro picado<br /> trozos de chicharr&oacute;n al gusto<br /> sal al gusto</p>\r\n<p><br /> <strong>Presentaci&oacute;n</strong></p>\r\n<p>tortillas de harina al gusto<br /> jitomates cortados en gajos, al gusto<br /> chile de &aacute;rbol cortado en rodajas, al gusto</p>', 5, 45, 2, 'saladas_18', 0, 2, 0),
(91, 'Molletes de rajas con elote y champiñones', 10, 12, '<p><strong>Rajas con elote</strong></p>\r\n<p>&bull; Caliente el aceite y acitrone la cebolla. Incorpore los granos de elote y las rajas, a&ntilde;ada sal y retire del fuego. Reserve.</p>\r\n<p><br /> <strong>Champi&ntilde;ones</strong></p>\r\n<p>&bull; Caliente en un sart&eacute;n el aceite y sofr&iacute;a la cebolla. A&ntilde;ada los champi&ntilde;ones, deje cocer durante 2 minutos, agregue el epazote, sal y retire del fuego. Reserve.</p>\r\n<p><br /> <strong>Salsa pico de gallo</strong></p>\r\n<p>&bull; Mezcle todos los ingredientes y reserve.</p>\r\n<p><br /> <strong>Molletes</strong></p>\r\n<p>&bull; Precaliente el horno a 160 &ordm;C.<br /> &bull; Quite un poco de migaj&oacute;n a las mitades de bolillo y &uacute;ntelas con frijoles refritos. Agregue a 4 mitades las rajas con elote y a las restantes, los champi&ntilde;ones. Espolvoree los molletes con queso.<br /> &bull; Coloque en una charola los molletes y horn&eacute;elos hasta que se doren ligeramente y el queso se funda.</p>\r\n<p><br /> <strong>Presentaci&oacute;n</strong></p>\r\n<p>&bull; Sirva calientes y acompa&ntilde;e con la salsa pico de gallo.</p>', '<p>Ingredientes para 4 porciones:</p>\r\n<p>&nbsp;</p>\r\n<p><strong>Rajas con elote</strong></p>\r\n<p>1 cucharada de aceite<br />1/4 de cebolla fileteada<br />1/2 taza de granos de elote precocidos<br /> 2 chiles poblano asados pelados, sin semillas ni venas y cortados en rajas<br /> sal al gusto</p>\r\n<p><br /> <strong>Champi&ntilde;ones</strong></p>\r\n<p>1 cucharada de aceite<br />1/4 de cebolla fileteada<br /> 10 champi&ntilde;ones fileteados<br /> 4 hojas de epazote picadas<br /> sal al gusto</p>\r\n<p><br /> <strong>Salsa pico de gallo</strong></p>\r\n<p>3 jitomates maduros cortados en cubos<br />1/2 taza de cebolla picada finamente<br /> 1/3 de taza de cilantro picado finamente<br /> 1 1/2 cucharadas de chile serrano verde picado finamente<br /> sal al gusto</p>\r\n<p><br /> <strong>Molletes</strong></p>\r\n<p>4 bolillos partidos por la mitad<br /> 1 taza de frijoles refritos<br /> 250 g de queso tipo manchego rallado</p>', 15, 15, 2, 'saladas_19', 0, 1, 0),
(92, 'Omelette con salsa de huitlacoche', 10, 12, '<p><strong>Omelette</strong></p>\r\n<p>&bull; Bata en&eacute;rgicamente 2 huevos con sal y pimienta hasta que queden espumosos. Caliente 1 cucharada de aceite en un sart&eacute;n peque&ntilde;o a fuego bajo y vierta los huevos; deje que cuajen ligeramente y coloque un poco de queso. Doble los bordes hacia el centro para cubrir el relleno y termine la cocci&oacute;n. Deber&aacute; obtener al final un omelette a&uacute;n cremoso por dentro. Repita el mismo procedimiento con el resto de los huevos.</p>\r\n<p><br /> <strong>Salsa de huitlacoche</strong></p>\r\n<p>&bull; Caliente el aceite o la mantequilla y acitrone la cebolla; a&ntilde;ada el huitlacoche, el epazote, el caldo de pollo y sal. Deje sobre el fuego durante 5 minutos y licue. Verifique la sal y reserve.</p>\r\n<p><br /> <strong>Presentaci&oacute;n</strong></p>\r\n<p>&bull; Coloque en un plato el omelette, acompa&ntilde;e con la salsa de huitlacoche, la lechuga y las rajas con elote.</p>', '<p>Ingredientes para 4 porciones:</p>\r\n<p>&nbsp;</p>\r\n<p><strong>Omelette</strong></p>\r\n<p>8 huevos<br /> sal y pimienta al gusto<br /> 4 cucharadas de aceite<br /> 300 g de queso manchego rallado</p>\r\n<p><br /> <strong>Salsa de huitlacoche</strong></p>\r\n<p>2 cucharadas de aceite o mantequilla<br />1/2 cebolla picada<br /> 250 g de huitlacoche<br /> 2 cucharadas de epazote picado<br /> 3 tazas de caldo de pollo<br /> sal al gusto</p>\r\n<p><br /> <strong>Presentaci&oacute;n</strong></p>\r\n<p>hojas de lechuga al gusto<br /> 1 receta de rajas con elote</p>', 5, 15, 2, 'saladas_20', 0, 3, 0),
(93, 'Pancita', 10, 12, '<p><strong>Pancita</strong></p>\r\n<p>&bull; Coloque en una olla de presi&oacute;n la panza, 1 1/2 litros de agua, la cebolla, los ajos y la sal. Tape y cueza por 20 minutos a partir de que comience a escapar el vapor. Deje enfriar. Corte la panza en cuadros de 3 cent&iacute;metros por lado y reserve.<br /> &bull; Ase los chiles por la parte m&aacute;s brillante y troc&eacute;elos.<br /> &bull; Caliente 1/8 de taza de aceite y fr&iacute;a la cebolla picada, los ajos, los chiles y las pimientas. A&ntilde;ada un poco de agua y cueza por unos minutos para que los chiles se suavicen; deje enfriar y licue hasta obtener una salsa muy tersa y cuele. <br /> &bull; Caliente el aceite restante y fr&iacute;a la salsa hasta que se cueza.<br /> &bull; Vierta a la pancita la salsa de chiles y mezcle; a&ntilde;ada el agua restante, deje hervir y baje el fuego; cueza por 10 minutos m&aacute;s. Unos instantes antes de servir, agregue el epazote y retire la cebolla y los ajos.</p>\r\n<p><br /> <strong>Presentaci&oacute;n</strong></p>\r\n<p>&bull; Deseche las ramas de epazote y sirva caliente. Acompa&ntilde;e con las guarniciones.</p>\r\n<p><br /> <em>La pancita es un platillo cl&aacute;sico para desayunar o almorzar. Se dice que es perfecta para reducir los estragos de una &ldquo;cruda&rdquo;.</em></p>', '<p>Ingredientes para 8 porciones:</p>\r\n<p>&nbsp;</p>\r\n<p><strong>Pancita</strong></p>\r\n<p>2 kg de panza de res, limpia<br /> 3 litros de agua<br />1/2 cebolla<br /> 2 dientes de ajo grandes<br /> 2 cucharadas de sal<br /> 4 chiles guajillo sin semillas ni venas<br /> 1 chile ancho sin semillas ni venas<br />1/4 de taza de aceite<br />1/2 cebolla picada<br /> 2 dientes de ajo grandes, troceados<br /> 4 pimientas negras<br /> 4 ramas de epazote</p>\r\n<p><br /> <strong>Presentaci&oacute;n</strong></p>\r\n<p>1/3 de taza de cebolla picada finamente<br /> chiles de &aacute;rbol secos al gusto<br /> or&eacute;gano seco al gusto<br /> limones partidos por la mitad, al gusto<br /> tortillas de ma&iacute;z al gusto</p>', 10, 40, 2, 'saladas_21', 0, 1, 0),
(94, 'Pastel azteca', 10, 12, '<p><strong>Salsa de chile pasilla y crema</strong></p>\r\n<p>&bull; Licue los chiles pasilla con el jitomate. Caliente la crema en una cacerola y vierta la salsa. Mezcle bien, agregue sal, y al primer hervor, retire del fuego.</p>\r\n<p><br /> <strong>Pastel</strong></p>\r\n<p>&bull; Precaliente el horno a 180 &ordm;C.<br /> &bull; Caliente el aceite y pase dentro de &eacute;l las tortillas una a una s&oacute;lo para que se suavicen.<br /> &bull; Arme el pastel en un refractario; coloque una capa de tortillas, una capa de salsa de chile pasilla y crema, un poco de pollo, granos de elote, flor de calabaza, rajas de chile poblano y un poco de queso. Repita la operaci&oacute;n tres veces m&aacute;s terminando con salsa y queso.<br /> &bull; Hornee durante 20 minutos.</p>\r\n<p><br /> <strong>Presentaci&oacute;n</strong></p>\r\n<p>&bull; Corte el pastel en porciones individuales y sirva.</p>\r\n<p>&nbsp;</p>\r\n<p><em>La combinaci&oacute;n de los granos de elote, la flor de calabaza y el chile poblano es la que otorga el nombre de azteca a esta preparaci&oacute;n, por referirse a ingredientes de origen prehisp&aacute;nico.&nbsp;</em></p>', '<p>Ingredientes para 6 porciones:</p>\r\n<p>&nbsp;</p>\r\n<p><strong>Salsa de chile pasilla y crema</strong></p>\r\n<p>6 chiles pasilla asados, sin semillas ni venas y remojados<br /> 1 jitomate asado<br />1/2 litro&nbsp;de crema<br /> sal al gusto</p>\r\n<p><br /> <strong>Pastel</strong></p>\r\n<p>1/2 kg de tortillas de ma&iacute;z<br /> 100 ml de aceite<br /> 1 taza de pollo cocido y deshebrado<br /> 1 taza de granos de elote cocidos<br /> 10 flores de calabaza limpias y troceadas<br /> 3 chiles poblano asados, pelados, sin semillas ni venas y cortados en rajas<br />1/2 kg de queso Chihuahua rallado</p>\r\n<p><br /> <strong>Material espec&iacute;fico</strong></p>\r\n<p>1 refractario grande o 6 individuales</p>', 15, 25, 2, 'saladas_22', 0, 2, 0),
(95, 'Puntas de filete en chipotle', 10, 12, '<p><strong>Puntas de filete</strong></p>\r\n<p>&bull; Salpimente las puntas de filete. Caliente en un sart&eacute;n de fondo grueso el aceite y selle las puntas. Reserve calientes.</p>\r\n<p><br /> <strong>Salsa</strong></p>\r\n<p>&bull; Ponga en el mismo sart&eacute;n el ajo y el caldo; con una pala de madera raspe todos los residuos del sart&eacute;n. Agregue los jitomates, el chipotle, y retire del fuego cuando el jitomate est&eacute; cocido. Licue, cuele, y vierta nuevamente en el sart&eacute;n. Deje reducir la salsa ligeramente y verifique la sal.<br /> &bull; A&ntilde;ada las puntas de filete a la salsa y mezcle.</p>\r\n<p><br /> <strong>Presentaci&oacute;n</strong></p>\r\n<p>&bull; Sirva con los frijoles charros, la cebolla y espolvoree el cilantro.</p>\r\n<p><br /> <em>Para un sabor diferente, sustituya el chile chipotle por chile mora, morita o cascabel.</em></p>', '<p>Ingredientes para 4 porciones:</p>\r\n<p>&nbsp;</p>\r\n<p><strong>Puntas de filete</strong></p>\r\n<p>600 g de puntas de filete de res limpio<br /> sal y pimienta al gusto<br /> 1 cucharada de aceite</p>\r\n<p><br /> <strong>Salsa</strong></p>\r\n<p>1 diente de ajo machacado<br /> 1 1/2 tazas de caldo de res<br /> 3 jitomates troceados<br /> 2 cucharadas de chiles chipotle adobados, picados<br /> sal al gusto</p>\r\n<p><br /> <strong>Presentaci&oacute;n</strong></p>\r\n<p>frijoles charros al gusto<br /> cebolla fileteada y desflemada, al gusto<br /> cilantro picado al gusto</p>', 5, 15, 3, 'saladas_23', 0, 1, 0),
(96, 'Quesadillas de quelites con requesón', 10, 12, '<p><strong>Relleno</strong></p>\r\n<p>&bull; Limpie los quelites para utilizar &uacute;nicamente las hojas. Obtendr&aacute; 1/2 kg aproximadamente.<br /> &bull; Caliente a fuego alto una cacerola con el aceite y sofr&iacute;a la cebolla con los chiles; a&ntilde;ada el ajo, cueza por 1 minuto m&aacute;s y agregue poco a poco los quelites mezclando bien entre cada tanda. A&ntilde;ada sal, tape la cacerola y deje cocer durante unos minutos. Retire del fuego y reserve.</p>\r\n<p><br /> <strong>Quesadillas</strong></p>\r\n<p>&bull; Caliente las tortillas en un comal y coloque en el centro de cada una reques&oacute;n y 2 cucharadas de quelites; doble las tortillas por la mitad y deje que se tuesten ligeramente por ambos lados.</p>\r\n<p><br /> <em>Acompa&ntilde;e estas quesadillas con alguna salsa roja o verde. La variedad de quelites en M&eacute;xico es grande: cenizo, malva, quintonil, lengua de vaca, huauzontle, entre otros. Elabore estas quesadillas con cualquiera de temporada. Todos ellos, adem&aacute;s de aportar sabor, son una fuente importante de minerales. Sustituya el reques&oacute;n por queso adobera o Cotija para un sabor m&aacute;s pronunciado.</em></p>', '<p>Ingredientes para 6 porciones:</p>\r\n<p>&nbsp;</p>\r\n<p><strong>Relleno</strong></p>\r\n<p>1 kg de quelite cenizo<br /> 3 cucharadas de aceite<br /> 1 taza de cebolla fileteada<br /> 4 chiles jalape&ntilde;o sin semillas ni venas cortados en rajas<br /> 1 cucharada de ajo picado<br /> sal al gusto</p>\r\n<p><br /> <strong>Quesadillas</strong></p>\r\n<p>12 tortillas de ma&iacute;z reci&eacute;n hechas<br /> 200 g de reques&oacute;n</p>', 10, 10, 1, 'saladas_24', 0, 1, 0),
(97, 'Queso panela asado con nopales', 10, 12, '<p><strong>Nopales</strong></p>\r\n<p>&bull; Ba&ntilde;e los nopales con el jugo de lim&oacute;n y salpimente.<br /> &bull; Ase los nopales en un sart&eacute;n con poco aceite. Retire los nopales y ase las</p>\r\n<p>re&shy;banadas de queso.</p>\r\n<p><br /> <strong>Salsa de chile pasilla</strong></p>\r\n<p>&bull; Licue todos los ingredientes excepto el aceite; caliente este &uacute;ltimo y fr&iacute;a la salsa por unos minutos, rectifique la saz&oacute;n y reserve.</p>\r\n<p><br /> <strong>Presentaci&oacute;n</strong></p>\r\n<p>&bull; Sirva acompa&ntilde;ado de las rebanadas de jitomate y la salsa de chile pasilla.</p>\r\n<p><br /> <em>El queso puede ser sustituido por alg&uacute;n otro como el asadero. Esta preparaci&oacute;n es ideal como complemento de un huarache.</em></p>', '<p>Ingredientes para 6 porciones:</p>\r\n<p>&nbsp;</p>\r\n<p><strong>Nopales</strong></p>\r\n<p>6 nopales tiernos<br /> el jugo de 1 lim&oacute;n<br /> sal y pimienta al gusto<br /> c/s de aceite<br /> 600 g de queso panela cortado en rebanadas gruesas</p>\r\n<p><br /> <strong>Salsa de chile pasilla</strong></p>\r\n<p>6 chiles pasilla asados, sin semillas ni venas y remojados<br /> 1 jitomate asado<br />1/2 cebolla asada<br /> 1 diente de ajo asado<br /> sal al gusto<br /> 2 cucharadas de aceite</p>\r\n<p>&nbsp;</p>\r\n<p><strong>Presentaci&oacute;n</strong></p>\r\n<p>2 jitomates bola cortados en rebanadas o en gajos<br /> chile pasilla frito y troceado al gusto</p>', 5, 10, 1, 'saladas_25', 0, 1, 0),
(98, 'Sopes', 10, 12, '<p><strong>Sopes</strong></p>\r\n<p>&bull; Mezcle la masa con un poco de agua y sal.<br /> &bull; Forme, entre 2 hojas de pl&aacute;stico, tortillas de 8 cent&iacute;metros de di&aacute;metro y 1/2 cent&iacute;metro de grosor con la ayuda de una tortilladora. P&oacute;ngalas sobre un comal caliente durante 3 minutos o hasta que la masa se torne opaca; volt&eacute;elas y cueza del otro lado por 3 minutos m&aacute;s. Deber&aacute; obtener 12 sopes.<br /> &bull; Haga a cada tortilla un borde presionando las orillas con los dedos. Debe quedar una pared alrededor, que es la que evitar&aacute; que el relleno se salga. Reserve los sopes en un recipiente tapados con un pa&ntilde;o hasta el momento de utilizarlos.</p>\r\n<p><br /> <strong>Salsa verde</strong></p>\r\n<p>&bull; Licue los tomates con los chiles hasta que obtenga una salsa tersa.<br /> &bull; A&ntilde;ada la cebolla, el ajo, el cilantro, la sal y licue nuevamente; reserve.</p>\r\n<p><br /> <strong>Salsa roja</strong></p>\r\n<p>&bull; Ase el jitomate, los chiles, la cebolla y los ajos; deje reposar por 20 minutos.<br /> &bull; Licue los ingredientes asados con sal; reserve.</p>\r\n<p><br /> <strong>Presentaci&oacute;n</strong></p>\r\n<p>&bull; Caliente el aceite en un sart&eacute;n y coloque boca abajo los sopes para que se calienten y se fr&iacute;an. Volt&eacute;elos y ponga sobre cada uno un poco de frijoles refritos, asegur&aacute;ndose de que cubran todo el interior.<br /> &bull; Agregue en cada uno un poco de salsa verde o roja, lechuga, a la mitad pollo y al resto, chorizo; al final cebolla, queso y crema a todos. <br /> &bull; Ret&iacute;relos del fuego, col&oacute;quelos sobre una servilleta de papel para retirar el exceso de grasa y sirva.</p>', '<p>Ingredientes para 6 porciones:</p>\r\n<p>&nbsp;</p>\r\n<p><strong>Sopes</strong></p>\r\n<p>400 g de masa para tortillas<br /> c/s de agua<br /> c/s de sal</p>\r\n<p><br /> <strong>Salsa verde</strong></p>\r\n<p>1/2 kg de tomates troceados<br /> 3 chiles de &aacute;rbol frescos<br />1/4 de taza de cebolla picada<br />1/2 cucharadita de ajo picado<br />1/4 de taza de cilantro fresco picado<br /> sal al gusto</p>\r\n<p><br /> <strong>Salsa roja</strong></p>\r\n<p>1/2 kg de jitomate<br /> 2 chiles de &aacute;rbol frescos, grandes<br />1/4 de cebolla picada<br /> 2 dientes de ajo chicos<br /> sal al gusto</p>\r\n<p><br /><strong> Presentaci&oacute;n</strong></p>\r\n<p>6 cucharadas de aceite para fre&iacute;r<br /> 1 taza de frijoles refritos<br /> 2 tazas de lechuga rebanada finamente<br /> 1 taza de pollo cocido y deshebrado<br /> 1 taza de chorizo frito<br />1/2 taza de cebolla picada<br />3/4 de taza de queso fresco rallado<br />1/2 taza de crema espesa (opcional)</p>', 30, 25, 2, 'saladas_26', 0, 2, 0),
(99, 'Tamal de cazuela', 10, 12, '<p><strong>Relleno</strong></p>\r\n<p>&bull; Cueza la carne con 2 ajos, sal y el agua durante 1 hora. Retire la carne del caldo y desh&eacute;brela; reserve por separado la carne y el caldo.<br /> &bull; Muela los chiles con el ajo restante, la cebolla y los jitomates. Si es necesario, agregue un poco de agua.<br /> &bull; Caliente la manteca en una cacerola y fr&iacute;a la salsa hasta que espese. Agregue la carne y cueza por 2 minutos m&aacute;s y rectifique la saz&oacute;n. Retire del fuego y reserve.</p>\r\n<p><br /> <strong>Tamal</strong></p>\r\n<p>&bull; Precaliente el horno a 180 &ordm;C.<br /> &bull; Disuelva la masa con un poco de caldo de cerdo. Hierva en una cacerola el caldo restante y agregue la masa disuelta sin dejar de mover. Cuando se despegue la masa de la cacerola, agregue la manteca y la sal. Deje por 10 minutos m&aacute;s en el fuego moviendo constantemente para que no se pegue al fondo de la cacerola.<br /> &bull; Arme el tamal vaciando en una cazuela de barro engrasada con manteca la mitad de la masa y distribuy&eacute;ndola muy bien. Coloque el relleno y vierta la otra mitad de la masa, cubriendo el relleno.<br /> &bull; Coloque encima las hojas santa y hornee durante 50 minutos.</p>\r\n<p><br /><strong>Presentaci&oacute;n</strong></p>\r\n<p>&bull; Corte el tamal en porciones individuales y sirva.</p>', '<p>Ingredientes para 8 porciones:</p>\r\n<p>&nbsp;</p>\r\n<p><strong>Relleno</strong></p>\r\n<p>1/2 kg de carne maciza de cerdo<br /> 3 dientes de ajo<br /> sal al gusto<br /> 2 litros&nbsp;de agua<br /> 50 g de chiles mulato asados, sin semillas ni venas y remojados<br /> 50 g de chiles ancho asados, sin semillas ni venas y remojados<br /> 50 g de chiles pasilla asados, sin semillas ni venas y remojados<br /> 2 cucharadas de cebolla picada finamente<br /> 2 jitomates asados<br /> c/s de agua<br /> 3 cucharadas de manteca de cerdo</p>\r\n<p><br /> <strong>Tamal</strong></p>\r\n<p>1 kg de masa de ma&iacute;z<br /> el caldo de cerdo que obtuvo de la cocci&oacute;n de la carne<br /> 200 g de manteca de cerdo + c/s para engrasar<br /> 1 cucharada de sal<br /> 3 hojas santa</p>\r\n<p><br /> <strong>Material espec&iacute;fico</strong></p>\r\n<p>1 refractario grande u 8 individuales</p>', 15, 90, 2, 'saladas_27', 0, 3, 0),
(100, 'Tamal oaxaqueño', 10, 12, '<p><strong>Masa</strong></p>\r\n<p>&bull; Mezcle la manteca, el polvo para hornear y la sal; bata hasta que la manteca se torne blanca.<br /> &bull; A&ntilde;ada la harina de ma&iacute;z y 1 taza de caldo; bata hasta que todo se incorpore y est&eacute; bien mezclado (si la masa se ve o se siente seca, a&ntilde;ada el resto del caldo; debe quedar ligeramente aguada).</p>\r\n<p><br /> <strong>Relleno</strong></p>\r\n<p>&bull; Caliente el caldo de pollo y disuelva el mole. Mezcle el mole con las carnes, si es necesario a&ntilde;ada sal, y reserve.</p>\r\n<p><br /><strong>Ensamble</strong></p>\r\n<p>&bull; Coloque sobre un cuadro de hoja de pl&aacute;tano 1/3 de taza de masa y encima un poco del relleno.<br /> &bull; Cierre los tamales y col&oacute;quelos de forma horizontal dentro de la vaporera con agua. Cueza a fuego alto, espere a que salga el vapor de la olla, y a partir de ese momento cu&eacute;zalos por 50 minutos. Apague el fuego y deje reposar al menos durante 20 minutos.</p>\r\n<p><br /> <em>Se ha generalizado el designar a un tamal como oaxaque&ntilde;o cuando est&aacute; envuelto en hoja de pl&aacute;tano, sin importar mucho el relleno que contenga. En este caso, se opt&oacute; por un mole negro y carne de cerdo, a la manera tradicional de Oaxaca.</em></p>', '<p>Ingredientes para 8 porciones:</p>\r\n<p>&nbsp;</p>\r\n<p><strong>Masa</strong></p>\r\n<p>1 taza de manteca de cerdo<br /> 1 cucharada de polvo para hornear<br /> 1 cucharada de sal<br /> 1 kg de harina de ma&iacute;z para tamales<br /> 1 1/2 tazas de caldo de cerdo, pollo o agua</p>\r\n<p><br /> <strong>Relleno</strong></p>\r\n<p>2 tazas de caldo de pollo o agua<br /> 200 g de mole negro en pasta<br />1/2 kg de pierna de cerdo cocida y deshebrada<br /> 1 pechuga de pollo cocida y deshebrada<br /> sal al gusto</p>\r\n<p><br /> <strong>Ensamble</strong></p>\r\n<p>c/s de hojas de pl&aacute;tano asadas cortadas en cuadros grandes</p>\r\n<p><br /> <strong>Material espec&iacute;fico</strong></p>\r\n<p>1 vaporera</p>', 30, 60, 2, 'saladas_28', 0, 2, 0),
(101, 'Tamales de rajas', 10, 12, '<p><strong>Masa</strong></p>\r\n<p>&bull; Bata la manteca con la sal y el polvo para hornear hasta que blanquee.<br /> &bull; Mezcle la harina para tamales con la manteca batida. A&ntilde;ada el caldo y bata hasta obtener una mezcla homog&eacute;nea y esponjada. Reserve.</p>\r\n<p><br /> <strong>Tamal de rajas</strong></p>\r\n<p>&bull; Licue los jitomates, la cebolla y el ajo. Caliente en una cacerola el aceite y fr&iacute;a la salsa. Agregue sal y deje hervir durante 5 minutos. Deje enfriar.<br /> &bull; Tome un poco de la masa preparada y con una cuchara grande exti&eacute;ndala en una hoja de ma&iacute;z, ponga 3 o 4 rajas de chile jalape&ntilde;o al centro, 1 tira de queso, 1 hoja de epazote y un poco de salsa. Cierre el tamal sin apretar demasiado para que la masa no se salga por el orificio de la base de la hoja. Repita este paso con el resto de las hojas y los ingredientes.<br /> &bull; Acomode los tamales en una vaporera y cu&eacute;zalos durante 1 hora o hasta que &eacute;stos se despeguen f&aacute;cilmente de la hoja.</p>', '<p>Ingredientes para 8 porciones:</p>\r\n<p>&nbsp;</p>\r\n<p><strong>Masa</strong></p>\r\n<p>1 taza de manteca de cerdo<br /> 1 cucharadita de sal<br /> 1 cucharadita de polvo para hornear<br /> 1 kg de harina de ma&iacute;z para tamales<br /> 1 1/2 tazas de caldo de pollo o cerdo</p>\r\n<p><br /> <strong>Tamal de rajas</strong></p>\r\n<p>10 jitomates<br />1/2 cebolla<br /> 1 diente de ajo<br /> 3 cucharadas de aceite<br /> sal al gusto<br /> c/s de hojas de ma&iacute;z remojadas en agua caliente<br /> 8 chiles jalape&ntilde;o sin semillas ni venas, cortados en rajas<br />1/2 kg de queso fresco cortado en tiras<br /> c/s de hojas de epazote</p>\r\n<p><br /> <strong>Material espec&iacute;fico</strong></p>\r\n<p>1 vaporera</p>', 40, 75, 2, 'saladas_29', 0, 2, 0),
(102, 'Tamales verdes', 10, 12, '<p><strong>Masa</strong></p>\r\n<p>&bull; Bata la manteca con la sal y el polvo para hornear hasta que blanquee.<br /> &bull; Mezcle la harina para tamales con la manteca batida. A&ntilde;ada el caldo y bata hasta obtener una mezcla homog&eacute;nea y esponjada. Reserve.</p>\r\n<p><br /> <strong>Tamal verdes</strong></p>\r\n<p>&bull; Licue el tomate, las hojas de cilantro, la cebolla, el ajo, los chiles de &aacute;rbol y sal para obtener una salsa con un poco de textura. Cu&eacute;lela para que se escurra toda el agua y quede s&oacute;lo la pulpa; pruebe, ajuste la cantidad de sal y reserve.<br /> &bull; Tome un poco de la masa preparada y con una cuchara grande exti&eacute;ndala en una hoja de ma&iacute;z; sobre &eacute;sta disponga un poco de carne y una cucharada generosa de salsa. Cierre el tamal sin apretar demasiado para que la masa no se salga por el orificio de la base de la hoja. Repita este paso con el resto de las hojas y los ingredientes.<br /> &bull; Acomode los tamales en una vaporera y cu&eacute;zalos durante 1 hora o hasta que &eacute;stos se despeguen f&aacute;cilmente de la hoja.</p>', '<p>Ingredientes para 8 porciones:</p>\r\n<p>&nbsp;</p>\r\n<p><strong>Masa</strong></p>\r\n<p>1 taza de manteca de cerdo<br /> 1 cucharadita de sal<br /> 1 cucharadita de polvo para hornear<br /> 1 kg de harina de ma&iacute;z para tamales<br /> 1 1/2 tazas de caldo de pollo o cerdo</p>\r\n<p><br /> <strong>Tamal verdes</strong></p>\r\n<p>1/2 kg de tomate verde<br /> 1/3 de taza de hojas de cilantro picadas<br />1/2 cebolla<br /> 1 diente de ajo<br /> 4 chiles de &aacute;rbol frescos<br /> sal al gusto<br /> c/s de hojas de ma&iacute;z remojadas en agua caliente<br />1/2 kg de carne maciza de cerdo cocida y deshebrada</p>\r\n<p><br /> <strong>Material espec&iacute;fico</strong></p>\r\n<p>1 vaporera</p>', 40, 75, 2, 'saladas_30', 0, 2, 0),
(103, 'Tasajo con entomatadas', 10, 12, '<p><strong>Tasajo</strong></p>\r\n<p>&bull; Ase el tasajo en una parrilla caliente.</p>\r\n<p><br /> <strong>Entomatadas</strong></p>\r\n<p>&bull; Cueza el jitomate con la cebolla, el ajo, el chile y el agua; retire del fuego y deje enfriar.<br /> &bull; Licue todos los ingredientes con un poco del agua de cocci&oacute;n hasta obtener una salsa tersa y fina.<br /> &bull; Caliente el aceite en una cacerola y vierta el molido de jitomate. A&ntilde;ada el epazote, deje cocer durante unos minutos y verifique la sal.<br /> &bull; Sumerja una a una las tortillas en la salsa, ret&iacute;relas y d&oacute;blelas a la mitad. Sirva inmediatamente con el tasajo.</p>\r\n<p><br /> <strong>Presentaci&oacute;n</strong></p>\r\n<p>&bull; Sirva el tasajo, acompa&ntilde;e con los frijoles refritos y 2 entomatadas decoradas con el queso, los aros de cebolla y la crema.</p>\r\n<p><br /> <em>Al desayunar tasajo en el mercado Ju&aacute;rez, en la capital oaxaque&ntilde;a, se refleja en sus comerciantes el esp&iacute;ritu de camarader&iacute;a. La venta no se concentra en un s&oacute;lo vendedor, sino en varios: la persona que vende el tasajo y lo asa, la se&ntilde;ora que vende las tortillas hechas a mano y el que vende las ensaladas y acompa&ntilde;amientos, como salsas y limones. As&iacute; cada qui&eacute;n decide en d&oacute;nde adquirir su desayuno.</em></p>', '<p>Ingredientes para 6 porciones:</p>\r\n<p>&nbsp;</p>\r\n<p><strong>Tasajo</strong></p>\r\n<p>1 kg de tasajo</p>\r\n<p><br /> <strong>Entomatadas</strong></p>\r\n<p>1/2 kg de jitomate guaje<br />1/4 de cebolla<br /> 2 dientes de ajo<br /> 1 chile de agua o jalape&ntilde;o<br /> 1 litro&nbsp;de agua<br /> 2 cucharadas de aceite<br /> 1 rama de epazote<br /> sal al gusto<br /> 12 tortillas de ma&iacute;z pasadas por aceite caliente</p>\r\n<p><br /> <strong>Presentaci&oacute;n</strong></p>\r\n<p>1 1/2 tazas de frijoles refritos<br /> 100 g de queso fresco rallado<br />1/2 cebolla cortada en aros delgados<br /> 6 cucharadas de crema &aacute;cida</p>', 10, 15, 2, 'saladas_31', 0, 2, 0),
(104, 'Fruta con yogur y compota de capulín', 11, 12, '<p><strong>Compota de capul&iacute;n</strong></p>\r\n<p>&bull; Coloque todos los ingredientes en una olla y hi&eacute;rvalos hasta que los capulines se deshagan. Pase la mezcla por un colador presionando bien para que &uacute;nicamente queden las semillas en &eacute;l; des&eacute;chelas.<br /> &bull; Ponga la pulpa de capul&iacute;n a fuego bajo hasta que adquiera una consistencia de mermelada ligera; retire del fuego y deje enfriar.</p>\r\n<p>&nbsp;</p>\r\n<p><strong>Fruta con yogur</strong></p>\r\n<p>&bull; Parta la fruta en trozos medianos, del tama&ntilde;o de un bocado.</p>\r\n<p><br /><strong> Presentaci&oacute;n</strong></p>\r\n<p>&bull; Sirva la fruta acompa&ntilde;ada del yogur y la compota de capul&iacute;n.</p>\r\n<p><br /> <em>Puede sustituir el capul&iacute;n por higo. El capul&iacute;n es un fruto peque&ntilde;o, de color rojizo casi negro y de sabor dulce. El aprovechamiento de esta fruta es completo, ya que la semilla que contiene en el centro se tuesta con sal para venderla como botana junto con las pepitas de calabaza y los cacahuates; para consumirla, es necesario romper la semilla o &ldquo;huesito&rdquo; de capul&iacute;n para extraer la peque&ntilde;a almendra del interior.</em></p>', '<p>Ingredientes para 8 porciones:</p>\r\n<p>&nbsp;</p>\r\n<p><strong>Compota de capul&iacute;n</strong></p>\r\n<p>1 kg de capul&iacute;n maduro<br /> 1 taza de az&uacute;car<br /> 1 litro&nbsp;de agua</p>\r\n<p><br /> <strong>Fruta con yogur</strong></p>\r\n<p>1/2 mel&oacute;n pelado y sin semillas<br />1/2&nbsp;papaya pelada y sin semillas<br />1/2&nbsp;kg de fresa<br /> 4 tunas peladas<br /> 4 guayabas<br /> 4 toronjas peladas y cortadas en supremas<br /> 400 ml de yogur natural</p>', 5, 20, 2, 'dulces_01', 0, 1, 0),
(105, 'Gorditas de nata', 11, 12, '<p>&bull; Bata la nata con el az&uacute;car hasta que se disuelva; agregue los huevos uno a uno hasta integrarlos perfectamente. Despu&eacute;s, a&ntilde;ada la harina, la manteca, el polvo para hornear y la canela. Si la masa est&aacute; muy seca agregue un poco de leche.<br /> &bull; Estire la masa con un rodillo sobre una superficie enharinada hasta dejarla de 1 cent&iacute;metro de grosor y corte c&iacute;rculos.<br /> &bull; Cueza las gorditas en un comal a fuego medio hasta que est&eacute;n doradas.</p>\r\n<p><br /><em> Es com&uacute;n encontrar en las calles de la ciudad de M&eacute;xico a los vendedores de estas gorditas. Sin embargo, el hecho de prepararlas en casa, siempre resultar&aacute; en un m&eacute;rito digno de encomiarse.</em></p>', '<p>Ingredientes para 12 porciones:</p>\r\n<p>&nbsp;</p>\r\n<p>1 taza de nata<br />1/2 kg de az&uacute;car<br /> 5 huevos<br /> 1 kg de harina de trigo cernida + c/s para enharinar<br /> 1 cucharada de manteca de cerdo fundida<br /> 2 cucharaditas de polvo para hornear<br /> 1 cucharadita de canela en polvo<br /> c/s de leche</p>', 15, 10, 1, 'dulces_02', 0, 2, 0),
(106, 'Panqué de nata', 11, 12, '<p><strong>Panqu&eacute;</strong></p>\r\n<p>&bull; Precaliente el horno a 180 &ordm;C.<br /> &bull; Bata la nata con el az&uacute;car; cuando esta &uacute;ltima se haya disuelto, agregue los huevos uno a uno hasta integrarlos perfectamente. Posteriormente a&ntilde;ada la leche, la esencia de vainilla y la ralladura de naranja.<br /> &bull; Cierna la harina con el polvo para hornear y la sal e incorp&oacute;relos a la mezcla de nata.<br /> &bull; Vac&iacute;e la preparaci&oacute;n en un molde para panqu&eacute; engrasado y enharinado previamente. Hornee durante 45 minutos o hasta que al insertar un palillo en el centro del panqu&eacute; &eacute;ste salga limpio.</p>\r\n<p><br /> <em>&Eacute;ste es un panqu&eacute; muy suave, dulce e ideal para un regalo. Acomp&aacute;&ntilde;elo con un caf&eacute; de olla o un chocolate de agua para comenzar el d&iacute;a.</em></p>', '<p>Ingredientes para 8 porciones:</p>\r\n<p>&nbsp;</p>\r\n<p><strong>Panqu&eacute;</strong></p>\r\n<p>1 taza de nata<br /> 2 tazas de az&uacute;car<br /> 4 huevos<br /> 1 taza de leche<br /> 1 cucharada de esencia de vainilla<br /> la ralladura de 1 naranja<br /> 2 tazas de harina<br /> 3 cucharaditas de polvo para hornear<br /> 1 cucharadita de sal<br /> c/s de mantequilla y harina para cubrir el molde</p>\r\n<p><br /> <strong>Material espec&iacute;fico</strong></p>\r\n<p>1 molde para panqu&eacute;</p>', 15, 45, 2, 'dulces_03', 0, 2, 0),
(107, 'Polvorones', 11, 12, '<p><strong>Polvorones</strong></p>\r\n<p>&bull; Precaliente el horno a 180 &ordm;C.<br /> &bull; Mezcle el az&uacute;car con la manteca vegetal. Agregue el bicarbonato, el polvo para hornear, la ralladura, el jugo de naranja y los huevos. Incorpore poco a poco la harina hasta formar una masa uniforme.<br /> &bull; Estire la masa con un rodillo y sobre una superficie enharinada</p>\r\n<p>corte los pol&shy;vorones.<br /> &bull; Col&oacute;quelos en una charola y hornee durante 30 minutos; retire del horno y d&eacute;jelos reposar por 20 minutos.</p>\r\n<p><br /> <strong>Presentaci&oacute;n</strong></p>\r\n<p>&bull; Acompa&ntilde;e los polvorones con chocolate de leche.</p>\r\n<p><br /> <em>Los polvorones forman parte de la panader&iacute;a tradicional mexicana. Puede variar el sabor de &eacute;stos sustituyendo la naranja por canela o an&iacute;s.</em></p>', '<p>Ingredientes para 12 porciones:</p>\r\n<p>&nbsp;</p>\r\n<p><strong>Polvorones</strong></p>\r\n<p>1/2 kg de az&uacute;car<br />1/2 kg de manteca vegetal a temperatura ambiente<br /> 5 g de bicarbonato<br /> 35 g de polvo para hornear<br /> la ralladura de 1 naranja<br /> el jugo de media naranja<br /> 2 huevos<br /> 1 kg de harina + c/s para enharinar</p>\r\n<p><br /> <strong>Material espec&iacute;fico</strong></p>\r\n<p>1 charola para hornear</p>', 15, 30, 1, 'dulces_04', 0, 2, 0),
(108, 'Rollos de canela', 11, 12, '<p><strong>Base de levadura</strong></p>\r\n<p>&bull; Disuelva el az&uacute;car y la levadura en el agua. Deje reposar hasta que haga espuma.</p>\r\n<p>&nbsp;</p>\r\n<p><strong>Relleno</strong><br /> &bull; Mezcle los ingredientes y reserve.</p>\r\n<p><br /> <strong>Masa</strong></p>\r\n<p>&bull; Haga un volc&aacute;n con la harina y ponga al centro el huevo, la base de levadura, la leche, el az&uacute;car y la sal. Incorpore poco a poco todos los ingredientes. Mezcle hasta que obtenga una masa uniforme; agregue la mantequilla en trozos. Trabaje la masa golpe&aacute;ndola sobre la mesa, estir&aacute;ndola y dobl&aacute;ndola sobre s&iacute; misma; al principio estar&aacute; pegajosa, pero no es recomendable agregar m&aacute;s harina.<br /> &bull; P&oacute;ngala en un taz&oacute;n y c&uacute;brala con un pa&ntilde;o h&uacute;medo. Deje reposar alrededor de 1 hora o hasta que doble su volumen.<br /> &bull; Precaliente el horno a 180 &ordm;C.<br /> &bull; Coloque la masa en una superficie enharinada y amase ligeramente.</p>\r\n<p>Extien&shy;da formando un rect&aacute;ngulo de unos 40 x 25 cent&iacute;metros y de 1 cent&iacute;metro de grosor. Seguido esparza el relleno, enr&oacute;llelo y corte rebanadas de unos 4 cent&iacute;metros de grosor.<br /> &bull; Acomode las rebanadas en un molde circular previamente engrasado y enharinado, dejando un espacio entre ellas; cubra con un pa&ntilde;o h&uacute;medo y deje reposar en un lugar tibio hasta que doblen su volumen.<br /> &bull; Hornee durante 30 minutos o hasta que los rollos est&eacute;n cocidos y dorados. Si se empiezan a dorar y a&uacute;n no est&aacute;n cocidos, c&uacute;bralos con papel aluminio.</p>\r\n<p><br /> <strong>Glaseado</strong></p>\r\n<p>&bull; Mezcle los ingredientes poco a poco; agregue m&aacute;s agua si fuera necesario. Glasee los rollos al salir del horno.</p>', '<p>Ingredientes para 12 porciones:</p>\r\n<p>&nbsp;</p>\r\n<p><strong>Base de levadura</strong></p>\r\n<p>1 cucharada de az&uacute;car<br /> 3 cucharadas de levadura en polvo<br />1/2 taza de agua tibia</p>\r\n<p><br /> <strong>Relleno</strong></p>\r\n<p>1/2 taza de mantequilla en trozos a temperatura ambiente<br /> 1 1/4 tazas de az&uacute;car mascabado<br />1/4 de taza de az&uacute;car<br /> 2 cucharadas de canela molida</p>\r\n<p><br /> <strong>Masa</strong></p>\r\n<p>4 1/2 tazas de harina<br /> 1 huevo<br />3/4 de taza de leche tibia<br />1/2 taza de az&uacute;car<br /> 1 cucharadita de sal<br />1/2 taza de mantequilla a temperatura ambiente</p>\r\n<p><br /> Glaseado (opcional)</p>\r\n<p>1 taza de az&uacute;car glass<br /> 2 cucharadas de agua<br /> 1 cucharada de extracto de vainilla</p>\r\n<p><br /> <strong>Material espec&iacute;fico</strong></p>\r\n<p>1 molde circular</p>', 150, 30, 2, 'dulces_05', 0, 3, 0),
(109, 'Torrejas con miel de piloncillo', 11, 12, '<p><strong>Torrejas</strong></p>\r\n<p>&bull; Caliente la leche a fuego bajo por 10 minutos, sin que hierva, con la raja de canela y el az&uacute;car. Reserve.<br /> &bull; Disponga las rebanadas de pan sobre una charola; vierta encima la leche para remojarlas y refrigere.<br /> &bull; Enharine las rebanadas de pan, p&aacute;selas por el huevo y fr&iacute;alas en el aceite caliente por ambos lados. Cuando est&eacute;n doradas, ret&iacute;relas del aceite</p>\r\n<p>y es&shy;c&uacute;rralas sobre papel absorbente.</p>\r\n<p><br /> <strong>Miel de piloncillo</strong></p>\r\n<p>&bull; Mezcle todos los ingredientes y ponga sobre el fuego hasta que tenga una consistencia ligeramente espesa. Deje enfriar.</p>\r\n<p>&nbsp;</p>\r\n<p><strong>Presentaci&oacute;n</strong><br /> &bull; Acomp&aacute;&ntilde;elas con la miel de piloncillo y decore con los higos.</p>\r\n<p><br /> <em>Esta preparaci&oacute;n, de origen extranjero, sirve perfectamente para aprovechar los sobrantes de pan duro del d&iacute;a anterior.</em></p>', '<p>Ingredientes para 6 porciones:</p>\r\n<p>&nbsp;</p>\r\n<p><strong>Torrejas</strong></p>\r\n<p>1 litro&nbsp;de leche<br /> 1 raja de canela<br />1/2&nbsp;taza de az&uacute;car<br /> 12 rebanadas de pan duro<br />3/4 de taza de harina<br /> 5 huevos batidos ligeramente<br /> c/s de aceite para fre&iacute;r</p>\r\n<p><br /> <strong>Miel de piloncillo</strong></p>\r\n<p>1 cono grande de piloncillo<br /> 1 raja de canela<br /> 1 cucharadita de semillas de an&iacute;s<br /> el jugo de 1 naranja<br /> 2 tazas de agua</p>\r\n<p><br /><strong> Presentaci&oacute;n</strong></p>\r\n<p>higos cortados en cuartos, al gusto</p>', 10, 25, 2, 'dulces_06', 0, 2, 0),
(110, 'Uchepos', 11, 12, '<p><strong>Uchepos</strong></p>\r\n<p>&bull; Desgrane los elotes y licue los granos con un poco de la nata.<br /> &bull; Bata el resto de la nata con el az&uacute;car y el polvo para hornear; integre esta mezcla a los granos licuados.<br /> &bull; Coloque en cada hoja de elote una porci&oacute;n de la masa y forme los tamales.<br /> &bull; Cueza en la vaporera durante 1 hora o hasta que se despeguen f&aacute;cilmente de la hoja.</p>\r\n<p><br /> <strong>Presentaci&oacute;n</strong></p>\r\n<p>&bull; S&iacute;rvalos con la crema.</p>\r\n<p><br /> <em>Este tamal tradicional michoacano es perfecto para acompa&ntilde;arlo con un atole. Usualmente se consumen con crema, aunque en ocasiones tambi&eacute;n puede hacer  la funci&oacute;n de un platillo salado si se acompa&ntilde;a con salsa de jitomate y queso.</em></p>', '<p>Ingredientes para 12 porciones:</p>\r\n<p>&nbsp;</p>\r\n<p><strong>Uchepos</strong></p>\r\n<p>12 elotes tiernos<br /> 3 tazas de nata<br /> 1 taza de az&uacute;car<br /> 1 cucharada de polvo para hornear<br /> c/s de hojas de elote</p>\r\n<p><br /> <strong>Presentaci&oacute;n</strong></p>\r\n<p>crema &aacute;cida (opcional)</p>\r\n<p><br /> <strong>Material espec&iacute;fico</strong></p>\r\n<p>1 vaporera</p>', 20, 60, 2, 'dulces_07', 0, 2, 0),
(111, 'Atole de arroz', 12, 12, '<p>&bull; Enjuague ligeramente el arroz y cu&eacute;zalo con el agua hasta que est&eacute; suave. A&ntilde;ada la leche, el az&uacute;car y la canela, moviendo ocasionalmente hasta que espese ligeramente. Verifique el az&uacute;car y sirva caliente.</p>\r\n<p><br /> <em>Llamado tambi&eacute;n &ldquo;arrocito&rdquo; es t&iacute;pico de las ma&ntilde;anas de la ciudad de M&eacute;xico, donde casi de manera obligada es acompa&ntilde;ante de un tamal o de una &ldquo;guajolota&rdquo; que no es otra cosa que una torta de tamal.</em></p>', '<p>Ingredientes para 4-6 porciones:</p>\r\n<p>&nbsp;</p>\r\n<p>1/2 taza de arroz<br /> 2 tazas de agua<br /> 4 tazas de leche<br />3/4 de taza de az&uacute;car<br /> 1 raja de canela</p>', 5, 30, 1, 'bebidas_01', 0, 1, 0),
(112, 'Atole de cacahuate', 12, 12, '<p>&bull; Muela el cacahuate en seco. Mezcle con la leche y el az&uacute;car, vuelva a moler muy bien y cuele. Reserve.<br /> &bull; Disuelva la harina de ma&iacute;z en el agua y ponga en un recipiente sobre fuego bajo con la canela y la c&aacute;scara de naranja; mueva constantemente. Cuando espese, a&ntilde;ada el cacahuate molido con la leche; contin&uacute;e moviendo el atole hasta que espese de nuevo. Verifique el az&uacute;car, retire del fuego y sirva.</p>', '<p>Ingredientes para 4-6 porciones:</p>\r\n<p>&nbsp;</p>\r\n<p>125 g de cacahuates pelados y tostados<br /> 1 litro&nbsp;de leche<br /> 250 g de az&uacute;car<br /> 50 g de harina de ma&iacute;z<br />1/2 litro de agua<br /> 1 raja de canela<br /> 1 trozo de c&aacute;scara de naranja</p>', 10, 25, 1, 'bebidas_02', 0, 1, 0),
(113, 'Café de olla', 12, 12, '<p>&bull; Encaje los clavos en la naranja y haga incisiones en la superficie de la misma con un cuchillo.<br /> &bull; Caliente el agua en una olla; en cuanto hierva, agregue la naranja, la canela y el piloncillo; deje hervir a fuego medio por 10 minutos.<br /> &bull; A&ntilde;ada el caf&eacute;, y cuando hierva nuevamente, retire del fuego. Tape y</p>\r\n<p>deje re&shy;posar durante 5 minutos. Cuele y sirva.</p>', '<p>Ingredientes para 10-12 porciones:</p>\r\n<p>&nbsp;</p>\r\n<p>5 clavos<br /> 1 naranja entera<br /> 3 litros de agua<br /> 2 rajas de canela<br /> 1 cono grande de piloncillo, rallado o al gusto<br /> 1 taza de caf&eacute; de grano reci&eacute;n molido</p>', 5, 15, 1, 'bebidas_03', 0, 1, 0),
(114, 'Champurrado', 12, 12, '<p>&bull; Hierva en la mitad del agua la canela, el piloncillo y el chocolate hasta que estos &uacute;ltimos se disuelvan.<br /> &bull; Mezcle la masa en el resto del agua fr&iacute;a y cuele. A&ntilde;ada esta masa al agua endulzada con el piloncillo y el chocolate.<br /> &bull; Hierva a fuego bajo hasta obtener la consistencia deseada.</p>\r\n<p><br /> <em>&Eacute;sta es una de las formas m&aacute;s tradicionales de preparar el chocolate en Oaxaca. Acomp&aacute;&ntilde;elo con un pan de yema.</em></p>', '<p>Ingredientes para 4-6 porciones:</p>\r\n<p>&nbsp;</p>\r\n<p>5 tazas de agua<br /> 1 raja de canela<br /> 1 cono grande de piloncillo<br /> 1 tableta de chocolate de metate<br /> 1 taza de masa de ma&iacute;z</p>', 5, 20, 1, 'bebidas_04', 0, 1, 0),
(115, 'Chocolate de agua', 12, 12, '<p>&bull; Ponga a calentar el agua con la canela. Cuando hierva, agregue el chocolate; deje hervir hasta que se deshaga el chocolate.<br /> &bull; Retire del fuego y deseche la canela. Bata con el molinillo o con un batidor manual para que se forme espuma en la superficie. Sirva inmediatamente.</p>', '<p>Ingredientes para 4-6 porciones:</p>\r\n<p>&nbsp;</p>\r\n<p>1 litro&nbsp;de agua<br /> 1 raja de canela<br /> 100 g de chocolate de metate</p>', 10, 10, 1, 'bebidas_05', 0, 2, 0),
(116, 'Chocolate de leche', 12, 12, '<p>&bull; Ponga a hervir la leche con la canela. Agregue el chocolate y deje hervir hasta que se deshaga el chocolate.<br /> &bull; Retire del fuego y deseche la canela. Bata con el molinillo o con un batidor manual para que se forme espuma en la superficie. Sirva inmediatamente.</p>', '<p>Ingredientes para 4-6 porciones:</p>\r\n<p>&nbsp;</p>\r\n<p>1 litro&nbsp;de leche<br /> 1 raja de canela<br /> 100 g de chocolate de metate</p>', 10, 10, 1, 'bebidas_06', 0, 2, 0),
(117, 'Licuado de plátano con chocolate', 12, 12, '<p>&bull; Licue todos los ingredientes y sirva.</p>', '<p>Ingredientes para 4-6 porciones:</p>\r\n<p>&nbsp;</p>\r\n<p>1 litro de leche<br /> 2 pl&aacute;tanos<br /> 3 cucharadas de chocolate en polvo<br /> 1 cucharadita de esencia de vainilla<br /> 1 cucharadita de canela en polvo<br /> az&uacute;car al gusto</p>', 5, 0, 1, 'bebidas_07', 0, 1, 0),
(118, 'Batido de mango', 12, 12, '<p>&bull; Extraiga la pulpa de los mangos y deseche las c&aacute;scaras y las semillas.<br /> &bull; Licue la pulpa de los mangos con la leche y la crema de coco. Sirva inmediatamente.</p>\r\n<p><br /> <em>Si fuera necesario, agregue az&uacute;car al batido.</em></p>', '<p>Ingredientes para 4-6 porciones:</p>\r\n<p>&nbsp;</p>\r\n<p>2 mangos maduros<br /> 750 ml de leche<br /> 250 ml de crema de coco</p>', 5, 0, 1, 'bebidas_08', 0, 1, 0),
(119, 'Jugo verde', 12, 12, '<p>&bull; Muela todos los ingredientes y sirva el jugo acompa&ntilde;ado con el lim&oacute;n.</p>', '<p>Ingredientes para 4-6 porciones:</p>\r\n<p>&nbsp;</p>\r\n<p>1 litro&nbsp;de jugo de naranja<br /> 2 ramas de apio<br /> 1 taza de hojas de alfalfa<br /> 1 nopal tierno<br />1/2 rebanada de pi&ntilde;a<br /> 1 guayaba<br /> 2 cucharadas de ping&uuml;ica<br /> miel al gusto<br /> limones partidos a la mitad, al gusto</p>', 5, 0, 1, 'bebidas_09', 0, 1, 0);
INSERT INTO `recetas` (`id`, `titulo`, `id_categoria`, `id_app`, `procedimiento`, `ingredientes`, `preparacion`, `coccion`, `costo`, `foto`, `user_fav`, `dificultad`, `preparada`) VALUES
(120, 'Vampiro', 12, 12, '<p>&bull; Pase por un extractor de jugos el betabel y las zanahorias.<br /> &bull; Mezcle los 3 jugos y sirva.</p>', '<p>Ingredientes para 4-6 porciones:</p>\r\n<p>&nbsp;</p>\r\n<p>1 betabel pelado<br /> 2 zanahorias peladas<br /> 1 litro&nbsp;de jugo de naranja</p>', 10, 0, 1, 'bebidas_10', 0, 1, 0),
